[Wrappable(true)]
internal void setLocateBy()
{
    if (WHSLocDirTableFormLocateByLoadingFlight::instance().isEnabled())
    {
        switch (any2Enum(workTransType.selection()))
        {
            case WHSWorkTransType::Purch:
            case WHSWorkTransType::TransferReceipt:
            case WHSWorkTransType::ProdPut:
            case WHSWorkTransType::ProdProcessPut:
                WHSLocDirTable_LocateBy.visible(true);
                break;
            default:
                WHSLocDirTable_LocateBy.visible(false);
                break;
        }

        this.updateLocateBy(this.mustDisplayLocateByASN(any2Enum(workTransType.selection())), WHSLocDirLocateBy::ASN);
        this.updateLocateBy(this.mustDisplayLocateByMixedLP(any2Enum(workTransType.selection())), WHSLocDirLocateBy::MixedLP);
    }
    else
    {
        switch (any2Enum(workTransType.selection()))
        {
            case WHSWorkTransType::Purch:
                WHSLocDirTable_LocateBy.visible(true);
                break;


            case WHSWorkTransType::TransferReceipt:
            case WHSWorkTransType::ProdPut:
            case WHSWorkTransType::ProdProcessPut:
                WHSLocDirTable_LocateBy.visible(true);
                WHSLocDirTable_LocateBy.delete(enum2Str(WHSLocDirLocateBy::ASN));
                WHSLocDirTable_LocateBy.delete(enum2Str(WHSLocDirLocateBy::MixedLP));
                break;

            default:
                WHSLocDirTable_LocateBy.visible(false);
                break;
        }
    }
}

    [Wrappable(true)]
    internal boolean mustDisplayLocateByASN(WHSWorkTransType _workTransType)
    {
        return _workTransType == WHSWorkTransType::Purch
				|| (_workTransType == WHSWorkTransType::TransferReceipt && WHSTransferReceiptASNFlight::instance().isEnabled());
    }

[Wrappable(true)]
internal boolean mustDisplayLocateByMixedLP(WHSWorkTransType _workTransType)
{
    return _workTransType == WHSWorkTransType::Purch;
}

private void updateLocateBy(boolean _shouldAddValue, WHSLocDirLocateBy _locateByValue)
{
    if (_shouldAddValue)
    {
        if (WHSLocDirTable_LocateBy.find(enum2Str(_locateByValue)) == -1)
        {
            WHSLocDirTable_LocateBy.add(enum2Str(_locateByValue));
        }
    }
    else
    {
        if (WHSLocDirTable_LocateBy.find(enum2Str(_locateByValue)) != -1)
        {
            WHSLocDirTable_LocateBy.delete(enum2Str(_locateByValue));
        }
    }
}

