}

private void initWarehouseSelectionFields()
{
    WHSLocDirTable_ds.object(fieldNum(WHSLocDirTable, WarehouseGroupId)).mandatory(WHSLocDirTable.WarehouseGroupAll == WhsWarehouseGroupAll::Group);
    WHSLocDirTable_ds.object(fieldNum(WHSLocDirTable, InventSiteId)).mandatory(WHSLocDirTable.WarehouseGroupAll == WhsWarehouseGroupAll::Warehouse);
    WHSLocDirTable_ds.object(fieldNum(WHSLocDirTable, InventLocationId)).mandatory(WHSLocDirTable.WarehouseGroupAll == WhsWarehouseGroupAll::Warehouse);

    WHSLocDirTable_ds.object(fieldNum(WHSLocDirTable, WarehouseGroupId)).visible(WHSLocDirTable.WarehouseGroupAll == WhsWarehouseGroupAll::Group);
    WHSLocDirTable_ds.object(fieldNum(WHSLocDirTable, InventSiteId)).visible(WHSLocDirTable.WarehouseGroupAll == WhsWarehouseGroupAll::Warehouse);
    WHSLocDirTable_ds.object(fieldNum(WHSLocDirTable, InventLocationId)).visible(WHSLocDirTable.WarehouseGroupAll == WhsWarehouseGroupAll::Warehouse);
}

private boolean isLocatePackingQtyEnabled()
{
    boolean locatePackingQtyIsEnabled = whsLocDirTable.WorkType == WHSWorkType::Pick
        && WhsWorkTransactionType::singletonFromTypeOrDefault(whsLocDirTable.WorkTransType).isForOutboundShipment();

    return locatePackingQtyIsEnabled;
}

public void delete()
{
    super();

    // if this was the last record in the grid, disable appropriate
    // buttons on the current grid and the grids below
    if (whsLocDirTable.RecId == 0)
    {
        element.initButtons();
        element.updateControls();
    }
}

public void write()
{
    super();

    queryRun = new QueryRun(whsLocDirTable.LocDirQuery);
    queryRun.name(strFmt('%1', whsLocDirTable.RecId));

    whsLocDirTable.LocDirQuery = queryRun.pack();
}

public void executeQuery()
{
    workTransTypeRange.value(queryValue(WorkTransType.valueStr()));

    if (InventLocationId.valueStr() != "")
    {
        this.addInventLocationIdRangeInQuery();
    }

    super();
}

public void init()
{
    super();

    qbdsLocationDirective = this.query().dataSourceTable(tableNum(WHSLocDirTable));

    workTransTypeRange = qbdsLocationDirective.addRange(fieldNum(WHSLocDirTable, WorkTransType));
}

public void initValue()
{
    super();

    whsLocDirTable.WorkTransType = WorkTransType.selection();
    whsLocDirTable.LocSeqNum = whsLocDirTableForm.whsLocDirTable_initValue(whsLocDirTable);

    if (whsLocDirTable.WorkTransType == WHSWorkTransType::PackedContainerPicking)
    {
        whsLocDirTable.MultiSKU = NoYes::Yes;
        WHSLocDirTable.locationDirectiveScope(true, WhsLocationDirectiveScope::MultiItem);
        whsLocDirTable.WorkType	= WHSWorkType::Put;
    }
}

public int active()
{
    int ret = super();

    element.validateMoveButtons(this,
        buttonMoveUpLocation,
        buttonMoveDownLocation);

    boolean notPackedContainerPicking = whsLocDirTable.WorkTransType != WHSWorkTransType::PackedContainerPicking;

    whsLocDirAction_BatchEnabled.enabled(whsLocDirTable.WorkType != WHSWorkType::Put && notPackedContainerPicking);
    element.initButtonEditQueryLocation();

    WHSLocDirTable_ScopeDisplayMethod.setAllowedValues();

    element.initButtonsLocDirLines();

    element.initButtonsLocDirActions();
    element.initWarehouseSelectionFields();

    element.clearLocDirLineMaxSeq();

    whsLocDirLine_LocatePackingQty.enabled(element.isLocatePackingQtyEnabled());

    whsLocDirTable_DispositionCode.enabled(WHSLocDirTable::canDispositionCodeBeUsed(whsLocDirTable.WorkTransType));

    whsLocDirAction_UseFixedLocation.enabled(notPackedContainerPicking);
    whsLocDirAction_LocDirStrategy.enabled(notPackedContainerPicking);
    whsLocDirTable_MultiSKU.enabled(notPackedContainerPicking);
    whsLocDirTable_WorkType.enabled(notPackedContainerPicking);

    boolean standardLocateBy = whsLocDirTable.LocateBy == WHSLocDirLocateBy::Standard;
    boolean standardLocateByAndNotPackedContainerPicking = standardLocateBy && notPackedContainerPicking;

    whsLocDirLine_FromQty.enabled(standardLocateBy);
    whsLocDirLine_ToQty.enabled(standardLocateBy);
    whsLocDirLine_UnitID.enabled(standardLocateByAndNotPackedContainerPicking);
    whsLocDirLine_LocateQty.enabled(standardLocateByAndNotPackedContainerPicking);
    whsLocDirLine_RestrictByUOM.enabled(standardLocateByAndNotPackedContainerPicking);
    whsLocDirLine_RoundUpToHandlingUnit.enabled(standardLocateByAndNotPackedContainerPicking);
    whsLocDirLine_AllowSplit.enabled(standardLocateByAndNotPackedContainerPicking);
    whsLocDirLine_ImmediateReplenishmentTemplateId.visible(whsLocDirTableForm.checkEnableImmediateReplenishmentTemplateId(whsLocDirTable));

    element.setDispositionAndApplicableDispositionCodeFieldAccess();

    return ret;
}

