}

private str createWarehouseGroupFilter()
{
    WhsWarehouseGroup warehouseGroup;
    WhsWarehouseGroupLink warehouseGroupLink;
    str conditionForWarehouseGroup;

    StringBuilder stringBuilder = new StringBuilder();

    while select WarehouseGroupId from warehouseGroup
        exists join warehouseGroupLink
            where warehouseGroup.WarehouseGroupId == warehouseGroupLink.WarehouseGroupId
            && warehouseGroupLink.InventLocationId == InventLocationId.valueStr()
    {
        conditionForWarehouseGroup = strFmt('(%1.%2 == "%3") ||',
            qbdsLocationDirective.name(),
            fieldstr(WHSLocDirTable, WarehouseGroupId),
            warehouseGroup.WarehouseGroupId);

        stringBuilder.Append(conditionForWarehouseGroup);
    }

    return stringBuilder.ToString();
}

private void addInventLocationIdRangeInQuery()
{
    inventLocationIdRange = SysQuery::findOrCreateRange(qbdsLocationDirective, fieldNum(WHSLocDirTable, InventLocationId));

    str warehouseGroupFilter = this.createWarehouseGroupFilter();

    str locationDirectivesLocationIdCondition = strFmt('(%1.%2 == "%3")',
        qbdsLocationDirective.name(),
        fieldstr(WHSLocDirTable, InventLocationId),
        InventLocationId.valueStr());

    str warehouseGroupAllIsAllCondition = strFmt('(%1.%2 == %3)',
        qbdsLocationDirective.name(),
        fieldstr(WHSLocDirTable, WarehouseGroupAll),
        enum2int(WhsWarehouseGroupAll::All));

    inventLocationIdRange.value(
        strfmt('(%1 %2 || %3)',
        warehouseGroupFilter,
        locationDirectivesLocationIdCondition,
        warehouseGroupAllIsAllCondition));
}

