void clicked()
{
    super();

    SysQueryRun sysQueryRun = WHSFormHelper::initializeQueryRunForEditing(
        whsLocDirTable.LocDirQuery,
        strFmt('%1', whsLocDirTable.RecId),
        whsLocDirTable.defaultLocationDirectiveQueryRun());

    sysQueryRun.initEditQueryFormParameter(WHSLocDirTable);

    if (sysQueryRun.prompt())
    {
        whsLocDirTable.LocDirQuery = sysQueryRun.pack();              
        whsLocDirTable.write();

        headerRangePart.refresh();

    }

    // For backwards compatibility
    queryRun = new QueryRun(whsLocDirTable.LocDirQuery);
}

