public boolean validate()
{
    boolean ret = super();

    switch (this.valueStr())
    {
        case enum2str(WHSWorkTransType::CycleCount):
            ret = checkFailed("@SYS4010124");
            break;
        case enum2str(WHSWorkTransType::WorkCancel):
            ret = checkFailed("@SYS4010123");
            break;
    }

    return ret;
}

public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        element.initButtons();

        qbdsLocationDirective.clearRange(fieldNum(WHSLocDirTable, InventLocationId));

        whsLocDirTable_ds.executeQuery();

        element.updateControls();
    }

    return ret;
}

