private void removeAllowedValue(WhsLocationDirectiveScope _value)
{
    if (this.valueStr() != enum2str(_value))
    {
        this.delete(enum2str(_value));
    }
}

private void resetAllowedValues()
{
    SysDictEnum dictEnum = SysDictEnum::newName(enumStr(WhsLocationDirectiveScope));
    this.items(0);
    for (int i = 0; i < dictEnum.values(); i++)
    {
        this.add(dictEnum.index2Label(i));
    }
    this.removeAllowedValue(WhsLocationDirectiveScope::Unknown);
}

public void setAllowedValues()
{
    this.resetAllowedValues();

    if (!WHSLocDirTable.isSingleItemLocationDirectiveScopeSupported())
    {
        this.removeAllowedValue(WhsLocationDirectiveScope::SingleItem);
    }

    if (!WHSLocDirTable.isSingleOrderLocationDirectiveScopeSupported())
    {
        this.removeAllowedValue(WhsLocationDirectiveScope::SingleOrder);
    }

    if (!WHSLocDirTable.isAllLocationDirectiveTypeSupported())
    {
        this.removeAllowedValue(WhsLocationDirectiveScope::All);
    }
}

