void clicked()
{
    if (!whsLocDirAction.LocDirName)
    {
        warning("@WAX3095");
        return;
    }

    super();

    // For backwards compatibility
    queryRun = new QueryRun(whsLocDirAction.LocDirQuery);

    boolean useInventDimOfInventSumForDefaultQuery = WHSLocDirAction::useInventoryDimensionOfInventSum() 
                                                     && WHSLocDirAction::useInventoryDimensionOfInventSumQuery(queryRun.query());

    SysQueryRun queryRunActions = WHSFormHelper::initializeQueryRunForEditing(
        whsLocDirAction.LocDirQuery,
        strFmt("@WAX:LocationDirectiveLineActionQueryName", whsLocDirAction.RefRecId, whsLocDirAction.RecId),
        whsLocDirAction.defaultLocationQueryRun(0, useInventDimOfInventSumForDefaultQuery));

    queryRunActions.query().dataSourceTable(tableNum(WMSLocation)).clearDynalinks();
    queryRunActions.initEditQueryFormParameter(WHSLocDirAction);

    if (queryRunActions.prompt())
    {
        // For backwards compatibility
        queryRun = queryRunActions;

        whsLocDirTableForm.writeWHSLocDirAction(whsLocDirAction, queryRunActions.pack());
        lineRangePart.refresh();
        lineSortingPart.refresh();

    }
}

void clicked()
{
    super();

    MultiSelectionHelper multiSelectHelper = MultiSelectionHelper::construct();
    multiSelectHelper.parmDatasource(whsLocDirAction_DS);

    List locDirActions = new List(Types::Record);
    WHSLocDirAction selectedRecord = multiSelectHelper.getFirst();

    while (selectedRecord)
    {
        locDirActions.addEnd(selectedRecord);
        selectedRecord = multiSelectHelper.getNext();
    }

    WHSLocationDirectiveActionCopier::copyLocationDirectiveActions(locDirActions);

    whsLocDirAction_DS.research(false);
}
