[Form]
public class WHSLocationLimit extends FormRun
{
    InventDimCtrl_Frm_ProductDim   inventDimFormSetup;
    RefRecId                       allowedContainerTypesId;

}

void init()
{
    super();

    element.updateDesign(InventDimFormDesignUpdate::Init);
}

Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    switch (mode)
    {
        case InventDimFormDesignUpdate::Init           :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_ProductDim::newFromForm(element);
            }
            inventDimFormSetup.parmSkipOnHandLookUp(true);
        // do continue
        case InventDimFormDesignUpdate::Active         :
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemIdProductDimensionsOnly(WHSLocationLimitVariant.ItemId));
            inventDimFormSetup.formSetControls(true, true);
            break;

        case InventDimFormDesignUpdate::FieldChange    :
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemIdProductDimensionsOnly(WHSLocationLimitVariant.ItemId));
            inventDim.clearNotSelectedDim(inventDimFormSetup.parmDimParmEnabled());
            inventDimFormSetup.formSetControls(true, true);

            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

private void initWarehouseSelectionFieldsForItem()
{
    WHSLocationLimit_ds.object(fieldNum(WHSLocationLimit, InventLocationId)).enabled(WHSLocationLimit.WarehouseGroupAll == WhsWarehouseGroupAll::Warehouse);
    WHSLocationLimit_ds.object(fieldNum(WHSLocationLimit, InventLocationId)).mandatory(WHSLocationLimit.WarehouseGroupAll == WhsWarehouseGroupAll::Warehouse);

    WHSLocationLimit_ds.object(fieldNum(WHSLocationLimit, WarehouseGroupId)).enabled(WHSLocationLimit.WarehouseGroupAll == WhsWarehouseGroupAll::Group);
    WHSLocationLimit_ds.object(fieldNum(WHSLocationLimit, WarehouseGroupId)).mandatory(WHSLocationLimit.WarehouseGroupAll == WhsWarehouseGroupAll::Group);

    WHSLocationLimit_ds.object(fieldNum(WHSLocationLimit, WMSLocationId)).enabled(WHSLocationLimit.WarehouseGroupAll == WhsWarehouseGroupAll::Warehouse);
}

private void initWarehouseSelectionFieldsForVariant()
{
    WHSLocationLimitVariant_ds.object(fieldNum(WHSLocationLimitVariant, InventLocationId)).enabled(WHSLocationLimitVariant.WarehouseGroupAll == WhsWarehouseGroupAll::Warehouse);
    WHSLocationLimitVariant_ds.object(fieldNum(WHSLocationLimitVariant, InventLocationId)).mandatory(WHSLocationLimitVariant.WarehouseGroupAll == WhsWarehouseGroupAll::Warehouse);

    WHSLocationLimitVariant_ds.object(fieldNum(WHSLocationLimitVariant, WarehouseGroupId)).enabled(WHSLocationLimitVariant.WarehouseGroupAll == WhsWarehouseGroupAll::Group);
    WHSLocationLimitVariant_ds.object(fieldNum(WHSLocationLimitVariant, WarehouseGroupId)).mandatory(WHSLocationLimitVariant.WarehouseGroupAll == WhsWarehouseGroupAll::Group);

    WHSLocationLimitVariant_ds.object(fieldNum(WHSLocationLimitVariant, WMSLocationId)).enabled(WHSLocationLimitVariant.WarehouseGroupAll == WhsWarehouseGroupAll::Warehouse);
}

private void initWarehouseSelectionFieldsForContainerType()
{
    WHSLocationLimitContainerType_ds.object(fieldNum(WHSLocationLimitContainerType, InventLocationId)).enabled(WHSLocationLimitContainerType.WarehouseGroupAll == WhsWarehouseGroupAll::Warehouse);
    WHSLocationLimitContainerType_ds.object(fieldNum(WHSLocationLimitContainerType, InventLocationId)).mandatory(WHSLocationLimitContainerType.WarehouseGroupAll == WhsWarehouseGroupAll::Warehouse);

    WHSLocationLimitContainerType_ds.object(fieldNum(WHSLocationLimitContainerType, WarehouseGroupId)).enabled(WHSLocationLimitContainerType.WarehouseGroupAll == WhsWarehouseGroupAll::Group);
    WHSLocationLimitContainerType_ds.object(fieldNum(WHSLocationLimitContainerType, WarehouseGroupId)).mandatory(WHSLocationLimitContainerType.WarehouseGroupAll == WhsWarehouseGroupAll::Group);
}

public int active()
{
    int ret;

    ret = super();

    if (WHSLocationLimit.AllowUnlimitedQty)
    {
        UnitId.enabled(false);
        Qty.enabled(false);
    }
    else
    {
        UnitId.enabled(true);
        Qty.enabled(true);
    }

    element.initWarehouseSelectionFieldsForItem();

    return ret;
}

