public boolean validateWrite()
{
    WHSLocationLimitVariant.InventDimIDProductDim = InventDim::findOrCreate(InventDim).InventDimId;

    boolean ret = super();

    WHSLocationLimitForm::validateReplenPickStrategy(WHSLocationLimitVariant.InventLocationId);

    return ret;
}

void write()
{
    ttsbegin;

    InventDim newInventDim = InventDim::findOrCreate(InventDim);
    WHSLocationLimitVariant.InventDimIDProductDim = newInventDim.InventDimId;

    super();

    if (WHSLocationLimitVariant.InventDimIDProductDim != InventDim.InventDimId)
    {
        InventDim.data(newInventDim);
        InventDim_DS.setCurrent();
    }

    ttscommit;
}

public int active()
{
    int ret;

    ret = super();

    element.updateDesign(InventDimFormDesignUpdate::Active);

    if (WHSLocationLimitVariant.AllowUnlimitedQty)
    {
        WHSLocationLimitVariant.Qty = 0;
        WHSLocationLimitVariant_Qty.enabled(false);
    }
    else
    {
        WHSLocationLimitVariant_Qty.enabled(true);
    }

    element.initWarehouseSelectionFieldsForVariant();

    return ret;
}

public void lookup(FormControl _formControl, str _filterStr)
{
    InventLocation::lookupWHSWarehouses(_formControl, '');
}

void modified()
{
    super();

    element.updateDesign(InventDimFormDesignUpdate::FieldChange);
    WHSLocationLimitVariant.UnitId = WHSCatchWeightHelper::inventHandlingUnitId(WHSLocationLimitVariant.ItemId);
}

public void lookup(FormControl _formControl, str _filterStr)
{
    InventTable::lookupItem(_formControl, true);
}

public void lookup(FormControl _formControl, str _filterStr)
