}

public int active()
{
    int ret;

    ret = super();

    allowedContainerTypesId = WHSLocationLimitContainerType.findAllowedContainerTypes();

    WHSLocationLimitContainerType_Qty.enabled(!WHSLocationLimitContainerType.AllowUnlimitedQty);

    element.initWarehouseSelectionFieldsForContainerType();

    return ret;
}

