public boolean modified()
{
    boolean ret = super();

    if (this.checked())
    {
        WHSLocationLimit.UnitId = '';
        WHSLocationLimit.Qty = 0;
        UnitId.enabled(false);
        Qty.enabled(false);
    }
    else
    {
        UnitId.enabled(true);
        Qty.enabled(true);
    }

    return ret;
}

public boolean modified()
{
    boolean ret = super();

    element.initWarehouseSelectionFieldsForVariant();

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (this.checked())
    {
        WHSLocationLimitVariant.Qty = 0;
        WHSLocationLimitVariant_Qty.enabled(false);
        WHSLocationLimitVariant.UnitId = '';
        WHSLocationLimitVariant_UnitId.enabled(false);
    }
    else
    {
        WHSLocationLimitVariant_Qty.enabled(true);
        WHSLocationLimitVariant_UnitId.enabled(true);
    }

    return ret;
}

public boolean modified()
{
    boolean ret = super();

    element.initWarehouseSelectionFieldsForContainerType();

    return ret;
}

public void lookup()
{
    WHSLocationLimitContainerType::lookupLocProfileIdsWithAllowedConType(this);
}

public boolean modified()
{
    boolean ret;

    if (WHSLocationLimitContainerType_LocProfileId.text() != WHSLocationLimitContainerType.LocProfileId)
    {
        allowedContainerTypesId = WHSLocationLimitContainerType.findAllowedContainerTypes(WHSLocationLimitContainerType_LocProfileId.text());
    }

    ret = super();

    return ret;
}

public void lookup()
{
    WHSContainerType::lookupContainerTypeByLocProfile(this, allowedContainerTypesId);
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (this.checked())
    {
        WHSLocationLimitContainerType_Qty.enabled(false);
    }
    else
    {
        WHSLocationLimitContainerType_Qty.enabled(true);
    }

    return ret;
}
