Method.Source:
[Form]
public class WHSLocationProfile extends FormRun
{
    internal boolean canShowInventDimParmTab;

}

private void enableReplenishmentOverflowFields()
{
    if (WHSLocationProfile.ReplenishmentExceedLocationCapacity)
    {
        WHSLocationProfile_ds.object(fieldNum(WHSLocationProfile, WorkAvailabilityThresholdType)).enabled(true);

        if (WHSLocationProfile.WorkAvailabilityThresholdType == WHSWorkAvailabilityThresholdType::Percent)
        {
            WHSLocationProfile_ds.object(fieldNum(WHSLocationProfile, ReplenishmentOverflowCapacityPercent)).enabled(true);
            WHSLocationProfile_ds.object(fieldNum(WHSLocationProfile, ReplenishmentOverflowCapacityQty)).enabled(false);
            WHSLocationProfile_ds.object(fieldNum(WHSLocationProfile, ReplenishmentOverflowCapacityUnit)).enabled(false);
        }
        else
        {
            WHSLocationProfile_ds.object(fieldNum(WHSLocationProfile, ReplenishmentOverflowCapacityPercent)).enabled(false);
            WHSLocationProfile_ds.object(fieldNum(WHSLocationProfile, ReplenishmentOverflowCapacityQty)).enabled(true);
            WHSLocationProfile_ds.object(fieldNum(WHSLocationProfile, ReplenishmentOverflowCapacityUnit)).enabled(true);
        }
    }
    else
    {
        WHSLocationProfile_ds.object(fieldNum(WHSLocationProfile, WorkAvailabilityThresholdType)).enabled(false);
        WHSLocationProfile_ds.object(fieldNum(WHSLocationProfile, ReplenishmentOverflowCapacityPercent)).enabled(false);
        WHSLocationProfile_ds.object(fieldNum(WHSLocationProfile, ReplenishmentOverflowCapacityQty)).enabled(false);
        WHSLocationProfile_ds.object(fieldNum(WHSLocationProfile, ReplenishmentOverflowCapacityUnit)).enabled(false);
    }
}

private void enableLocationProductDimensionMixingFlag()
{
    if (canShowInventDimParmTab
        &&  WHSLocationProfile.AllowMixedItems)
    {
        WHSLocationProfile.EnableLocationProductDimensionSpecificMixing = NoYes::No;
        WHSLocationProfile_EnableLocationProductDimensionSpecificMixing.enabled(false);
    }
    else
    {
        WHSLocationProfile_EnableLocationProductDimensionSpecificMixing.enabled(true);
    }
}

private void enableLocationProductDimensionMixingFields()
{
    if (canShowInventDimParmTab 
        && WHSLocationProfile.EnableLocationProductDimensionSpecificMixing)
    {
        if (!InventDimParm)
        {
            inventDimParm_ds.create();
        }

        InventDimParm.initFromInventDimFixed(WHSLocationProfile.ProductDimFixed);

        TabPageProductDimensionMixing.enabled(true);
    }
    else
    {
        TabPageProductDimensionMixing.enabled(false);
        WHSLocationProfile.ProductDimFixed = 0;
        InventDimParm.initFromInventDimFixed(WHSLocationProfile.ProductDimFixed);
        InventDimParm_ds.refresh();
    }
}

/// <summary>
/// Checks if any product dimensions are enabled.
/// </summary>
/// <returns>
/// True if any product dimensions are enabled, otherwise false.
/// </returns>
private boolean isAnyProductDimensionEnabled()
{
    return !InventDim::dimProductDimensionEnabledFieldList().empty();
}

/// <summary>
/// Initializes form and sets global variables.
/// </summary>
public void init()
{
    super();

    canShowInventDimParmTab = this.isAnyProductDimensionEnabled();

    if (canShowInventDimParmTab)
    {
        WHSLocationProfile_EnableLocationProductDimensionSpecificMixing.visible(true);
        TabPageProductDimensionMixing.visible(true);
    }
    else
    {
        WHSLocationProfile_EnableLocationProductDimensionSpecificMixing.visible(false);
        TabPageProductDimensionMixing.visible(false);
    }
}

public int active()
{
    int ret;

    ret = super();

    whsLocationProfile_ds.object(fieldNum(WHSLocationProfile, AllowMixedBatches)).allowEdit(!whsLocationProfile.mustAllowMixedBatches());

    WHSLocationProfile_AllowedContainerTypeGroup.enabled(WHSLocationProfile.LPControlled == NoYes::Yes);

    element.enableReplenishmentOverflowFields();
    element.enableLocationStatusFields();
    element.enableLocationProductDimensionMixingFlag();
    element.enableLocationProductDimensionMixingFields();
    element.enableLocationLicensePlatePositioningFields();

    return ret;
}

/// <summary>
/// Enable or disable replenishment overflow fields.
/// </summary>
public void modified()
{
    super();

    element.enableReplenishmentOverflowFields();
}

/// <summary>
/// Enable or disable replenishment overflow fields.
/// </summary>
public void modified()
{
    super();

    element.enableReplenishmentOverflowFields();
}

/// <summary>
/// Initializes <c>InventDimParm</c> datasource and enables/disables dimension fields dependent upon whether or not their respective config key is enabled.
/// </summary>
public void init()
{
    super();

    if (canShowInventDimParmTab)
    {
        InventDimParm.setAllProductDimensions();
    }
}

/// <summary>
/// Writes the <c>InventDimParm</c> record and updates the <c>WHSLocationProfile</c> record with the new <c>ProductDimFixed</c> value
/// </summary>
public void write()
{
    super();

    WHSLocationProfile.ProductDimFixed = InventDimFixedClass::inventDimParm2InventDimFixed(inventDimParm);
    WHSLocationProfile.write();
}

public boolean modified()
{
    boolean ret;

    ret = super();

    WHSLocationProfile_AllowedContainerTypeGroup.enabled(this.checked());

    element.enableLocationLicensePlatePositioningFields();

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    WHSLocationProfile_ds.object(fieldNum(WHSLocationProfile, DisplayMobileDeviceLocationLicensePlatePositioning))
        .enabled(WHSLocationProfile.EnableLocationLicensePlatePositioning);

    return ret;
}
