public ItemId itemIdToMove()
{
    return WHSProductsWithWorkExceptionsView.ItemId;
}

public InventDimId inventDimIdMoveFrom()
{
    InventDim inventDimLocationAndAbove = InventDim::find(WHSProductsWithWorkExceptionsView.InventDimId);
    inventDimLocationAndAbove.wMSLocationId = WMSLocation.wMSLocationId;
    inventDimLocationAndAbove = InventDim::findOrCreate(inventDimLocationAndAbove);

    return inventDimLocationAndAbove.inventDimId;
}

public WMSLocationId wmsLocationIdMoveTo()
{
    return WHSLocationWithWorkExceptionView.WMSLocationId;
}

public InventQty qtyToMove()
{
    return max(TotalWorkLineInventQtyNeededAtLoc.realValue() - TotalIncomingWorkInventQty.realValue(), 0);
}

public void onMovementWorkCreated()
{
    // Reread and refresh the current product record, so we update the total inventory available for reservation from other locations.
    WHSProductsWithWorkExceptionsView_DS.reread();
    WHSProductsWithWorkExceptionsView_DS.refresh();

    // Reread and refresh the current on-hand record, so the available quantity for that particular location is updated.
    WHSInventReserve_DS.reread();
    WHSInventReserve_DS.refresh();
}

