[SysClientCacheDataMethod]
public display WHSInventQtyWork displayTotalWorkLineInventQty(WHSProductsWithWorkExceptionsView _productWithWorkExceptions)
{
    // The data source query already contains the join conditions necessary.
    var queryRun = new QueryRun(WHSWorkLineOpen_Q);
    queryRun.query().dataSourceTable(tableNum(WHSWorkLineView)).addSelectionField(fieldNum(WHSWorkLineView, InventQtyWork), SelectionField::Sum);
    if (queryRun.next())
    {
        return queryRun.get(tableNum(WHSWorkLineView)).getFieldValue(fieldStr(WHSWorkLineView, InventQtyWork));
    }
    return 0;
}

public void init()
{
    super();

    this.queryBuildDataSource().addSelectionField(fieldNum(WHSWorkLineView, ItemId));

    this.queryBuildDataSource().addRange(fieldNum(WHSWorkLineView, WorkType)).value(queryValue(WHSWorkType::Pick));
    this.queryBuildDataSource().addRange(fieldNum(WHSWorkLineView, WorkStatus)).value(queryValue(WHSWorkStatus::Open));

    this.queryBuildDataSource().addDynalink(fieldNum(WHSWorkLineView, ItemId), WHSProductsWithWorkExceptionsView, fieldNum(WHSProductsWithWorkExceptionsView, ItemId));
    this.queryBuildDataSource().addDynalink(fieldNum(WHSWorkLineView, InventDimId), WHSProductsWithWorkExceptionsView, fieldNum(WHSProductsWithWorkExceptionsView, InventDimId));
    this.queryBuildDataSource().addDynalink(fieldNum(WHSWorkLineView, InventLocationId), WHSProductsWithWorkExceptionsView, fieldNum(WHSProductsWithWorkExceptionsView, InventLocationId));
    this.queryBuildDataSource().addDynalink(fieldNum(WHSWorkLineView, WMSLocationId), WHSProductsWithWorkExceptionsView, fieldNum(WHSProductsWithWorkExceptionsView, WMSLocationId));
}

public void init()
{
    super();

    // Adding the sorting here and not in WHSWorkLineView.init, since the WHSLoadTable data source is not yet initialized then.
    this.query().dataSourceTable(tableNum(WHSLoadTable)).addOrderByField(fieldNum(WHSLoadTable, LoadSchedShipUTCDateTime));
    this.query().dataSourceTable(tableNum(WHSWorkLineView)).addOrderByField(fieldNum(WHSWorkLineView, WorkId));
    this.query().dataSourceTable(tableNum(WHSWorkLineView)).addOrderByField(fieldNum(WHSWorkLineView, LineNum));
}

public void init()
{
    super();

    this.queryBuildDataSource().addDynalink(fieldNum(WHSInventReserve, ItemId), WHSProductsWithWorkExceptionsView, fieldNum(WHSProductsWithWorkExceptionsView, ItemId));

    this.queryBuildDataSource().addRange(fieldNum(WHSInventReserve, HierarchyLevel)).status(RangeStatus::Locked);

    var qtyRange = this.queryBuildDataSource().addRange(fieldNum(WHSInventReserve, AvailPhysical));
    qtyRange.value('>0');
    qtyRange.status(RangeStatus::Locked);
}

public void executeQuery()
{
    var locationLevel = WHSReservationHierarchyProvider::construct().getLocationHierarchyLevel(InventTable::find(WHSProductsWithWorkExceptionsView.ItemId));

    SysQuery::findOrCreateRange(this.queryBuildDataSource(), fieldNum(WHSInventReserve, HierarchyLevel)).value(queryValue(locationLevel));

    var qbdsInventDim = this.query().dataSourceTable(tableNum(InventDim));
    qbdsInventDim.clearRanges();
    var locationRange = SysQuery::findOrCreateRange(qbdsInventDim, fieldNum(InventDim, wMSLocationId));
    locationRange.value(SysQuery::valueNot(WHSProductsWithWorkExceptionsView.WMSLocationId));
    locationRange.status(RangeStatus::Locked);

    InventDim::queryAddRangeCriteria(qbdsInventDim, InventDim::find(WHSProductsWithWorkExceptionsView.InventDimId));

    super();
}

public boolean locationHasOpenWorkException(WMSLocation _wmsLocation, WHSProductsWithWorkExceptionsView _product)
{
    WHSWorkExceptionLog workExceptionLog;
    WHSWorkLine         workLine;

    select firstonly RecId from workExceptionLog
        where workExceptionLog.InventLocationId == _wmsLocation.inventLocationId
            && workExceptionLog.WMSLocationId == _wmsLocation.wMSLocationId
            && workExceptionLog.Status == WHSWorkExceptionLogStatus::Open
            && (workExceptionLog.ExceptionType == WHSExceptionType::ShortPick
            || workExceptionLog.ExceptionType == WHSExceptionType::Work)
        exists join workLine
            where workLine.WorkId == workExceptionLog.WorkId
                && workLine.LineNum == workExceptionLog.LineNum
                && workLine.ItemId == _product.ItemId
                && workLine.InventDimId == _product.InventDimId
                && workLine.LoadLineRefRecId != 0;

    return (workExceptionLog.RecId != 0);
}

[SysClientCacheDataMethod]
public display container hasOpenWorkException(WMSLocation _wmsLocation)
{
    WHSProductsWithWorkExceptionsView_DS.observe();

    if (this.locationHasOpenWorkException(_wmsLocation, WHSProductsWithWorkExceptionsView))
    {
        return ImageReference::constructForSymbol(ImageReferenceSymbol::Warning).pack();
    }

    return conNull();
}

public int active()
{
    int ret;

    ret = super();

    OnHandOpenWorkExceptions.enabled(this.locationHasOpenWorkException(WMSLocation, WHSProductsWithWorkExceptionsView));

    return ret;
}

public void clicked()
{
    WHSWorkExceptionLog::updateStatusForWorkExceptionLogEntiresForLocation(WHSLocationWithWorkExceptionView.wmsLocation(), WHSWorkExceptionLogStatus::Closed);

    super();
}

