public void clicked()
{
    var workTableForm = new WhsWorkTableForm();
    workTableForm.cancelLine_clicked(WHSWorkLineOpen_DS);

    // Reread and refresh the current location record, so if this was the last work line cancelled, we'll get the min sched load date and time cleared along with count of work lines.
    WHSLocationWithWorkExceptionView_DS.reread();
    WHSLocationWithWorkExceptionView_DS.refresh();

    // Reread and refresh the current product record, so we update the total invent qty required on this location.
    WHSProductsWithWorkExceptionsView_DS.reread();
    WHSProductsWithWorkExceptionsView_DS.refresh();

    super();
}
