[Form]
public class WHSMobileAppFlowStepListPage extends FormRun implements WHSIMobileAppFlowStepContextProvider, WHSIMobileAppTextLanguageIdProvider, WHSIMobileAppFlowStepPromotedFieldsRefresh
{
    #Task

    private boolean isDetailMode;

    [FormObservable]
    private LanguageId selectedLanguage = infolog.language();

    private QueryBuildRange qbrTitleForInputLanguageId;
    private QueryBuildRange qbrTitleForConfirmationLanguageId;
    private QueryBuildRange qbrTmpRefRecId;
    private QueryBuildRange qbrTitleForInputRefRecId;
    private QueryBuildRange qbrTitleForConfirmationRefRecId;
    private QueryBuildRange qbrInstructionForInputRefRecId;
    private QueryBuildRange qbrInstructionForConfirmationRefRecId;
    private QueryBuildRange qbrDetourFlowStepRefRecId;

}

private void addMobileAppTextRange(QueryBuildDataSource _qbds, WHSMobileAppTextType _textType)
{
    _qbds.addRange(fieldNum(WHSMobileAppText, Type)).value(SysQuery::value(_textType));
}

public LanguageId languageId()
{
    return selectedLanguage;
}

public WHSMobileAppFlowStep flowStep()
{
    return whsMobileAppFlowStep;
}

public void postPromotedFieldsRefresh()
{
    this.updatePromotedFieldNamesLabel();
}

private void updatePromotedFieldNamesLabel()
{
    var fieldNames = whsMobileAppFlowStep.displayPromotedFields();

    if (!fieldNames)
    {
        fieldNames = "@WAX24";
    }

    PromotedFieldNames.text(fieldNames);
}

[Hookable(false)]
internal void updateLanguageIdRange()
{
    qbrTitleForInputLanguageId.value(SysQuery::value(selectedLanguage));
    qbrTitleForConfirmationLanguageId.value(SysQuery::value(selectedLanguage));
}

[Hookable(false)]
internal void updateRefRecIdRange()
{
    qbrTmpRefRecId.value(SysQuery::value(whsMobileAppFlowStep.RecId));
    qbrTitleForInputRefRecId.value(SysQuery::value(whsMobileAppFlowStep.RecId));
    qbrTitleForConfirmationRefRecId.value(SysQuery::value(whsMobileAppFlowStep.RecId));
    qbrInstructionForInputRefRecId.value(SysQuery::value(whsMobileAppFlowStep.RecId));
    qbrInstructionForConfirmationRefRecId.value(SysQuery::value(whsMobileAppFlowStep.RecId));
    qbrDetourFlowStepRefRecId.value(SysQuery::value(whsMobileAppFlowStep.RecId));
}

private void switchToDetailsView()
{
    AddOverride.visible(false);
    CreateDefaultButton.visible(false);
    element.updateRefRecIdRange();
    LanguageTable_ds.executeQuery();

    boolean isDefaultStep = whsMobileAppFlowStep.isDefaultFlowStep();
    TranslatedTextsRemoveButton.enabled(languageTable.RecId > 0 && !isDefaultStep);
    ViewStepTranslations.visible(languageTable.RecId > 0);

    isDetailMode = true;

    TabDetour.visible(!isDefaultStep);
    WHSMobileAppFlowStepDetour_ds.executeQuery();
    DetourRemoveButton.enabled(whsMobileAppFlowStepDetour.RecId > 0);
    SelectFieldsToSend.enabled(whsMobileAppFlowStepDetour.RecId > 0);

    TabPromotedFields.visible(!isDefaultStep);
    this.updatePromotedFieldNamesLabel();
}

private void switchToGridView()
{
    AddOverride.visible(true);
    CreateDefaultButton.visible(true);
    WHSMobileAppFlowStep_ds.research(true);
    isDetailMode = false;
}

public int task(int _taskId)
{
    int ret = super(_taskId);

    if (_taskId == #taskSwitchToDetailsView)
    {
        this.switchToDetailsView();
    }
    else if(_taskId == #taskSwitchToGridView)
    {
        this.switchToGridView();
    }

    return ret;
}

public void init()
{
    super();
}

public int active()
{
    int ret = super();

    RemoveOverride.enabled(!whsMobileAppFlowStep.isDefaultFlowStep());

    ShowAllStepsForMenuItem.enabled(!whsMobileAppFlowStep.isDefaultFlowStep() || (whsMobileAppFlowStep.isDefaultFlowStep() && ShowAllStepsForMenuItem.checked()));

    if (isDetailMode)
    {
        element.updateRefRecIdRange();
        LanguageTable_ds.executeQuery();
    }
    return ret;
}

public boolean validateDelete()
{
    boolean ret = super();

    if (!whsMobileAppFlowStep.isDefaultFlowStep())
    {
        if (this.recordsMarked().lastIndex() > 1)
        {
            ret = checkFailed("@WAX:MobileAppFlowStepDeleteMultipleWarning");
        }
        else
        {
            ret = checkFailed(strFmt("@WAX:MobileAppFlowStepDeleteWithMenuItemWarning", whsMobileAppFlowStep.StepId, whsMobileAppFlowStep.MenuItemName));
        }
    }
    else if (whsMobileAppFlowStep.existOverridingSteps())
    {
        if (this.recordsMarked().lastIndex() > 1)
        {
            ret = checkFailed("@WAX:MobileAppFlowStepDeleteMultipleWarning");
        }
        else
        {                        
            ret = checkFailed(strFmt("@WAX:MobileAppFlowStepDeleteOverriddenWarning", whsMobileAppFlowStep.StepId));
        }
    }

    return ret;
}

public void init()
{
    super();

    this.queryBuildDataSource().addRange(fieldNum(WHSMobileAppText, Type)).value(SysQuery::value(WHSMobileAppTextType::StepEnterValueTitle));
    qbrTitleForInputLanguageId = this.queryBuildDataSource().addRange(fieldNum(WHSMobileAppText, LanguageId));
    qbrTitleForInputLanguageId.value(SysQuery::value(selectedLanguage));
}

public void init()
