public int active()
{
    int ret = super();

    if (ret)
    {
        DetourRemoveButton.enabled(WHSMobileAppFlowStepDetour.RecId != 0);
        SelectFieldsToSend.enabled(WHSMobileAppFlowStepDetour.RecId != 0);
    }

    return ret;
}

public void clicked()
{
    ttsbegin;

    if (isDetailMode)
    {
        whsMobileAppFlowStep.deleteRelatedTexts('');
        whsMobileAppFlowStep.delete();
    }
    else
    {
        whsMobileAppFlowStep.deleteRelatedTexts(selectedLanguage);

        if (!whsMobileAppFlowStep.existRelatedTexts(''))
        {
            whsMobileAppFlowStep.delete();
        }
    }    

    ttscommit;

    WHSMobileAppFlowStep_ds.research(true);

    super();

    if (isDetailMode)
    {
        element.task(#taskSwitchToGridView);
    }
}

public void clicked()
{
    var originLanguageId = infolog.language();
    try
    {
        ttsbegin;

        var dataCreator = WHSMobileAppStepInstructionDefaultDataCreator::construct();
        dataCreator.createDefaultMobileAppFlowSteps();

        ttscommit;

        WHSMobileAppFlowStep_ds.research(true);
    }
    finally
    {
        infolog.language(originLanguageId);
    }
}

public void clicked()
{
    ttsbegin;

    whsMobileAppFlowStep.deleteRelatedTexts(LanguageTable.LanguageId);

    ttscommit;

    LanguageTable_ds.research(true);
    WHSMobileAppFlowStep_ds.research(true);

    super();
}

public void jumpRef()
{
    Args args = new Args();
    args.record(LanguageTable);
    args.caller(element);

    MenuFunction menuFunction = new MenuFunction(menuitemDisplayStr(WHSMobileAppFlowStepEditTranslations), MenuItemType::Display);
    menuFunction.run(args);
}

public void clicked()
{
    ttsbegin;

    WHSMobileAppFlowStepDetour::deleteDetourAndRelatedFields(whsMobileAppFlowStepDetour);

    ttscommit;

    WHSMobileAppFlowStepDetour_ds.research(true);
    WHSMobileAppFlowStep_ds.research(true);

    super();
}

public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        selectedLanguage = this.valueStr();
        element.updateLanguageIdRange();
        WHSMobileAppFlowStep_ds.executeQuery();
    }

    return ret;
}

protected void initialize()
{
    super();

    this.text(selectedLanguage);
}

public boolean modified()
{
    boolean ret = super();

    if (this.checked())
    {
        SetEnumerator se = WHSMobileAppFlowStep::getAllStepsRecIdForOneFlow(WHSMobileAppFlowStep.MenuItemName, selectedLanguage).getEnumerator();
        while (se.moveNext())
        {
            WHSMobileAppFlowStep_ds.query().dataSourceTable(tableNum(WHSMobileAppFlowStep)).addRange(fieldNum(WHSMobileAppFlowStep, RecId)).value(SysQuery::value(se.current()));
        }
    }
    else
    {
        WHSMobileAppFlowStep_ds.query().dataSourceName('WHSMobileAppFlowStep').clearRanges();
    }

    WHSMobileAppFlowStep_ds.executeQuery();

    return ret;
}

public void jumpRef()
{
    element.task(#taskSwitchToDetailsView);
}
