[Form]
public class WHSOnHand extends FormRun
{
    InventSiteId                     inventSiteId;
    InventLocationId                 inventLocationId;
    WMSLocationId                    wmsLocationId;

    str                              wmsLocationIdFilter;

    WHSOnHandForm                    whsOnHandForm;
    InventDimCtrl_Frm_WHSOnhand      inventDimFormSetup;

    InventAvailabilityFormCache      inventAvailabilityFormCache;

    #define.CurrentVersion(1)
    #localmacro.CurrentList
        inventSiteId,
        inventLocationId,
        wmsLocationId
    #endmacro

}

void filterWorkTable()
{
    [wmsLocationIdFilter, inventSiteId, inventLocationId] = whsOnHandForm.filterWorkTable(element.args().record());
}

public void init()
{
    whsOnHandForm = new WHSOnHandForm();

    super();

    this.processArgs();

    parmInventSiteId.text(inventSiteId);
    parmInventLocationId.text(inventLocationId);
    parmWMSLocationId.text(wmsLocationId);

    element.updateDesign(InventDimFormDesignUpdate::Init);

    inventAvailabilityFormCache = InventAvailabilityFormCache::construct();
}

public boolean mustLoadDataBasedOnSettings()
{
    return (controlRefreshAcrossLocations.value() || parmWMSLocationId.valueStr());
}

