public void setBottomRanges()
{
    ItemId                  headerItemvalue;
    QueryBuildDataSource    qbdsInventDimBottom, qbdsInventSumBottom;

    qbdsInventDimBottom = inventDimBottom_ds.queryBuildDataSource();

    //filter bottom based on the current active wmsLocation record
    qbdsInventDimBottom.clearRanges();
    qbdsInventDimBottom.addRange(fieldNum(InventDim, InventLocationId)).value(queryValue(wmsLocation.inventLocationId));
    qbdsInventDimBottom.addRange(fieldNum(InventDim, wmsLocationId)).value(queryValue(wmsLocation.wmsLocationId));

    qbdsInventSumBottom = inventSumBottom_ds.queryBuildDataSource();

    qbdsInventSumBottom.clearRanges();
    qbdsInventSumBottom.addRange(fieldNum(InventSum, PhysicalInvent)).value('>0');
    qbdsInventSumBottom.addRange(fieldNum(InventSum, ClosedQty)).value(queryValue(NoYes::No));

    headerItemvalue = headerItemId.valueStr();
    //if we have a single item on the location then add filter on that since it will speed up the query
    if (headerItemvalue && headerItemvalue != "@WAX402")
    {
        qbdsInventSumBottom.addRange(fieldNum(InventSum, itemId)).value(queryValue(headerItemvalue));
    }
}

void processArgs()
{
    if (element.args().record())
    {
        switch (element.args().dataset())
        {
            case tableNum(WHSWorkTable):
                this.filterWorkTable();
                controlRefreshAcrossLocations.value(NoYes::Yes);
                break;

            default:
                break;
        }
    }
    else
    {
        // If we are being opened with a caller then we don't load all the previous settings since we want to display data based on what was loaded from the filter
        xSysLastValue::getLast(this);
    }
}

/// <summary>
/// Called if changes have been made in the <c>InventDimParmFixed</c> form
/// This method refreshes the grid to reflect the changes
/// </summary>
void rightClickModifyQuery()
{
    element.inventDimSetupObject().parmDimParmVisible(element.inventDimSetupObject().parmDimParmVisibleGrid());
    element.inventDimSetupObject().rightClickModifyQueryGroupBy(inventSum_DS);

    inventSumBottom_DS.executeQuery();
}

void setRanges()
{
    // Clear all ranges
    wmsLocation_ds.queryBuildDataSource().clearRanges();
    inventDim_ds.queryBuildDataSource().clearRanges();
    inventSum_ds.queryBuildDataSource().clearRanges();

    // Add a range for the user selected Warehouse
    wmsLocation_ds.queryBuildDataSource().addRange(fieldNum(WMSLocation, InventLocationId)).value(queryValue(parmInventLocationId.valueStr()));

    if (wmsLocationIdFilter)
    {
        wmsLocation_ds.queryBuildDataSource().addRange(fieldNum(WMSLocation, wmsLocationId)).value(wmsLocationIdFilter);
    }
    else if (wmsLocationId)
    {
        wmsLocation_ds.queryBuildDataSource().addRange(fieldNum(WMSLocation, wmsLocationId)).value(queryValue(wmsLocationId));
    }

    // Add a range to only get locations with quantity.
    inventSum_ds.queryBuildDataSource().addRange(fieldNum(InventSum, PhysicalInvent)).value('>0');
    inventSum_ds.queryBuildDataSource().addRange(fieldNum(InventSum, ClosedQty)).value(queryValue(NoYes::No));
}

public boolean unpack(container _packedClass)
{
    boolean             ret;
    int                 version;
    int                 refreshAcrossLocations;

    version = conPeek(_packedClass, 1);

    switch (version)
    {
        case #CurrentVersion:
            [version, #CurrentList, refreshAcrossLocations] = _packedClass;
            ret = true;

            whsOnHandForm.parmInventLocationId(inventLocationId);
            whsOnHandForm.parmInventSiteId(inventSiteId);

            break;
    }

    controlRefreshAcrossLocations.value(refreshAcrossLocations);

    return ret;
}

/// <summary>
/// Updates the dimensions display object.
/// </summary>
/// <param name="_mode">
/// The state of the dimensions display form.
/// </param>
/// <exception cref="Exception::Error">
/// Incorrect parameter for the function.
/// </exception>
public void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm inventDimParmLocal;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            inventDimParmLocal.setAllProductDimensions();
            inventDimParmLocal.setAllTrackingDimensions();
            inventDimParmLocal.InventStatusFlag   = NoYes::Yes;
            inventDimParmLocal.LicensePlateFlag   = NoYes::Yes;
            element.inventDimSetupObject().parmDimParmVisibleGrid(inventDimParmLocal);
            btnQuarantine.multiSelect(true);

        // do continue
        case InventDimFormDesignUpdate::Active:
            element.inventDimSetupObject().formActiveSetup();
            element.inventDimSetupObject().formSetControls(true);

            var img = InvalidDimensionsWarning.image();

            WHSLocationLicensePlatePositioning_LicensePlatePosition.visible(this.isLicensePlatePositionVisible());

            break;

        default:
            throw error(strFmt("@SYS54195",funcName()));
    }
}

public int task(int _taskId)
{
    int ret;

    #Task
    switch (_taskId)
    {
        case #TaskRefresh:
            WMSLocation_ds.executeQuery();
            break;

        default:
            ret = super(_taskId);
            break;
    }

    return ret;
}

private boolean isLicensePlatePositionVisible()
{
    return DisplayDims_LicensePlateId.isVisible()
        && WMSLocation.WHSLocationProfile().EnableLocationLicensePlatePositioning;
}

public void displayOption(Common _record, FormRowDisplayOption _options)
{
    WMSLocation location = _record;

    if (showEmpty.checked() && location.whsLocationIdEmptyIncludeDelta(false))
    {
        _options.backColor(0x9CFF00); // Green
    }

    super(_record, _options);
}

 public int active()
{
     int ret;

     ret = super();

     inventSumBottom_ds.executeQuery();

     return ret;
 }

public void executeQuery()
{
    // Update the Detail query
    element.setRanges();

    inventAvailabilityFormCache.refreshCache();

    super();

    //if we have no locations then call the bottom to set ranges since active wont be called for that case
    if (!wmsLocation.wmsLocationId)
    {
        inventSumBottom_ds.executeQuery();
    }
}

