/// <summary>
/// Gets the physically available quantity based on the specified on hand record.
/// </summary>
/// <param name="_inventSum">
/// An inventory on hand record.
/// </param>
/// <returns>
/// The physically available quantity.
/// </returns>
public display InventQtyAvailPhysical availPhysical(InventSum _inventSum)
{
    return inventAvailabilityFormCache.availabilityFromSumJoinedWithDim(_inventSum, inventDimBottom_ds).availPhysical();
}

/// <summary>
/// Gets the physically available quantity for a catch weight item based on the specified on hand record.
/// </summary>
/// <param name = "_inventSum">
/// And inventory on hand record.
/// </param>
/// <returns>
/// The physically available quantity for a catch weight item.
/// </returns>
public display PdsCWAvailPhysical cwAvailPhysical(InventSum _inventSum)
{
    if (!PdsGlobal::pdsIsCWItem(_inventSum.ItemId))
    {
        return 0;
    }

    return inventAvailabilityFormCache.cwSpecificAvailabilityFromSumJoinedWithDim(_inventSum, inventDimBottom_ds).pdsCWAvailPhysical();
}

/// <summary>
/// Modifies the query to group by the fields selected in the <c>InventDimParmFixed</c> form
/// </summary>
public void executeQuery()
{
    element.setBottomRanges();
    element.inventDimSetupObject().modifyQuery(inventSumBottom_DS,inventDimBottom_DS);

    super();
}

