/// <summary>
/// Check if there if the selected dimension set creates a hole in the reservation hierarchy.
/// </summary>
/// <param name="_inventSum">
/// The <c>InventSum</c> datasource's table buffer.
/// </param>
/// <returns>
/// A warning icon if the dimension setup is invalid, otherwise a blank icon.
/// </returns>
public display container displayInvalidDimensionWarning(InventSum _inventSum)
{
    InventDim   inventDimLoc;
    container   invalidBlankDims;
    container   ret = conNull();

    inventDimLoc.data(_inventSum.joinChild());
    invalidBlankDims = WHSReservationHierarchyInventDimUtil::getNotAllowedBlankDimInHierarchy(_inventSum.inventTable(), inventDimLoc);

    if (invalidBlankDims != conNull())
    {
        ret = ImageReference::constructForSymbol(ImageReferenceSymbol::HighPriority).pack();
    }

    return ret;
}

/// <summary>
/// Find the available physical inventory for the displayed dimension set.
/// </summary>
/// <param name="_inventSum">
/// The <c>InventSum</c> datasource's table buffer.
/// </param>
/// <returns>
/// The available physical quantity for the exact dimension set displayed.
/// </returns>
public display InventQtyAvailPhysical displayAvailPhysExactDims(InventSum _inventSum)
{
    InventDim joinedInventDim;
    Qty       availPhysical;
    #ResAppl

    joinedInventDim.data(_inventSum.joinChild());
    availPhysical = WHSInventReserve::findByInventDim(_inventSum.ItemId, joinedInventDim).AvailPhysical;

    if (availPhysical == 0)
    {
        if (WHSReservationHierarchyInventDimUtil::getNotAllowedBlankDimInHierarchy(_inventSum.inventTable(), joinedInventDim) != conNull())
        {
            container image = ImageReference::constructForSymbol(ImageReferenceSymbol::HighPriority).pack();
            InvalidDimensionsWarning.imageLocation(SysImageLocation::Symbol);
            InvalidDimensionsWarning.normalImage(ImageReferenceSymbol::HighPriority);
        }
    }
    else
    {
        InvalidDimensionsWarning.normalImage('');
    }

    return availPhysical;
}

/// <summary>
/// Find the available physical inventory for the displayed dimension set for a catch weight item.
/// </summary>
/// <param name="_inventSum">
/// The <c>InventSum</c> datasource's table buffer.
/// </param>
/// <returns>
/// The available physical quantity for the exact dimension set displayed.
/// </returns>
public display PdsCWQty displayCWAvailPhysExactDims(InventSum _inventSum)
{
    if (!PdsGlobal::pdsIsCWItem(_inventSum.ItemId))
    {
        return 0;
    }

    InventDim joinedInventDim;
    PdsCWQty  cwAvailPhysical;
    #ResAppl

    joinedInventDim.data(_inventSum.joinChild());
    cwAvailPhysical = WHSInventReserve::findByInventDim(_inventSum.ItemId, joinedInventDim).CWAvailPhysical;

    if (cwAvailPhysical == 0)
    {
        if (WHSReservationHierarchyInventDimUtil::getNotAllowedBlankDimInHierarchy(_inventSum.inventTable(), joinedInventDim) != conNull())
        {
            container image = ImageReference::constructForSymbol(ImageReferenceSymbol::HighPriority).pack();
            InvalidDimensionsWarning.imageLocation(SysImageLocation::Symbol);
            InvalidDimensionsWarning.normalImage(ImageReferenceSymbol::HighPriority);
        }
    }
    else
    {
        InvalidDimensionsWarning.normalImage('');
    }

    return cwAvailPhysical;
}

