    {
        wmsLocation_ds.executeQuery();
    }

    return ret;
}

public void clicked()
{
    super();

    if (this.checked())
    {
        inventDim_ds.query().dataSourceTable(tableNum(InventDim)).enabled(false);
        inventSum_ds.query().dataSourceTable(tableNum(InventSum)).enabled(false);
    }
    else
    {
        inventDim_ds.query().dataSourceTable(tableNum(InventDim)).enabled(true);
        inventSum_ds.query().dataSourceTable(tableNum(InventSum)).enabled(true);
    }

    wmsLocation_ds.executeQuery();
}

public void clicked()
{
    super();

    if (element.mustLoadDataBasedOnSettings())
    {
        wmsLocation_ds.executeQuery();
    }
}

void clicked()
{
    MultiSelectionHelper helper = MultiSelectionHelper::construct();
    helper.parmDatasource(InventSumBottom_DS);

    whsOnHandForm.btnQuarantineMultiSelect_clicked(helper);

    inventAvailabilityFormCache.refreshCache();

    InventSumBottom_DS.executeQuery();
}
