private void refreshCallerForm(FormRun _formRun)
{
    #Task

    Object formRunObject;

    if (_formRun)
    {
        switch (_formRun.name())
        {
            case formStr(SalesTableListPage):
            case formStr(SalesTable):
                if (formHasMethod(_formRun,identifierStr(refreshTmsDisplayRouteCode)))
                {
                    formRunObject = _formRun;
                    formRunObject.refreshTmsDisplayRouteCode();
                }
                _formRun.task(#taskF5);
                break;
            default:
                break;
        }
    }
}

