/// <summary>
/// Adds shipment to a load
/// </summary>
public void addShipmentToLoad()
{
    loadPlanningWorkbenchForm.parmLoadPaysFreight(whsLoadTable.LoadPaysFreight);

    var dialog = new Dialog("@WAX309");
    var dialogLoadTemplate = dialog.addField(extendedTypeStr(WHSLoadTemplateId));
    dialogLoadTemplate.value(whsLoadTable.LoadTemplateId);

    if (whsLoadTable.LoadTemplateId)
    {
        dialogLoadTemplate.allowEdit(false);
    }

    dialog.run();

    if (dialog.closedOk())
    {
        WHSLoadTemplateId loadTemplateId = dialogLoadTemplate.value();
        loadPlanningWorkbenchForm.parmLoadTemplateId(loadTemplateId);

        this.assertWeightAndVolumeLimits(whsLoadTable, loadPlanningWorkbenchForm.parmLoadTemplateId());

        boolean restrictToSameWarehouseWhenCreatingLoads = this.restrictToSameWarehouseWhenCreatingLoads();
        boolean restrictAddShipmentWhenDiffWHS = WHSOutboundLPWRestrictAddShipmentToLoadWhenDiffWHSFlight::instance().isEnabled();

        InventLocationId currentInventLocationId = whsLoadTable.InventLocationId;

        boolean showWarningForDifferentWHS;

        WHSShipmentTable shipmentTable;
        int counter = 1;
        for (shipmentTable = whsShipmentTableUnassigned_ds.getFirst(true) ? whsShipmentTableUnassigned_ds.getFirst(true) : whsShipmentTableUnassigned_ds.cursor();
                shipmentTable;
                shipmentTable = whsShipmentTableUnassigned_ds.getNext())
        {
            if (!restrictAddShipmentWhenDiffWHS 
                || !restrictToSameWarehouseWhenCreatingLoads 
                || shipmentTable.InventLocationId == currentInventLocationId)
            {
                loadPlanningWorkbenchForm.addShipmentToLoad(whsLoadTable, shipmentTable, counter);
                ++counter;
            }
            else
            {
                showWarningForDifferentWHS = true;
            }
        }

        if (showWarningForDifferentWHS)
        {
            warning(strFmt("@WAX:LPWDifferentWarehousesWarning", currentInventLocationId));
        }
    }
}

protected void addShipmentToNewLoad()
{
    var dialog = new Dialog("@WAX309");
    var dialogLoadTemplate = dialog.addField(extendedTypeStr(WHSLoadTemplateId));
    dialogLoadTemplate.value(loadPlanningWorkbenchForm.defaultShipmentLoadTemplateId(whsShipmentTableUnassigned));
    dialog.run();

    if (dialog.closedOk())
    {
        ttsbegin;

        loadPlanningWorkbenchForm.parmLoadTemplateId(dialogLoadTemplate.value());

        if (!loadPlanningWorkbenchForm.parmLoadTemplateId())
        {
            throw error("@WAX2008");
        }

        WHSLoadTable loadTable;
        loadTable.setLoadId();

        this.assertWeightAndVolumeLimits(loadTable, loadPlanningWorkbenchForm.parmLoadTemplateId()); 

        boolean restrictToSameWarehouseWhenCreatingLoads = this.restrictToSameWarehouseWhenCreatingLoads();
        boolean restrictAddShipmentWhenDiffWHS = WHSOutboundLPWRestrictAddShipmentToLoadWhenDiffWHSFlight::instance().isEnabled();

        WHSShipmentTable firstShipment = whsShipmentTableUnassigned_ds.getFirst(true) ? whsShipmentTableUnassigned_ds.getFirst(true) : whsShipmentTableUnassigned_ds.cursor();
        InventLocationId currentInventLocationId = firstShipment.InventLocationId;

        boolean showWarningForDifferentWHS;
        Set shipmentInLoad = new Set(Types::String);

        WHSShipmentTable shipmentTable;
        int counter = 0;
        for (shipmentTable = whsShipmentTableUnassigned_ds.getFirst(true) ? whsShipmentTableUnassigned_ds.getFirst(true) : whsShipmentTableUnassigned_ds.cursor();
                shipmentTable;
                shipmentTable = whsShipmentTableUnassigned_ds.getNext())
        {
            if (!restrictAddShipmentWhenDiffWHS 
                || !restrictToSameWarehouseWhenCreatingLoads 
                || shipmentTable.InventLocationId == currentInventLocationId)
            {
                loadTable = loadPlanningWorkbenchForm.addShipmentToNewLoadPopulateTable(shipmentTable, loadTable, counter);
                ++counter;
                shipmentInLoad.add(shipmentTable.ShipmentId);
            }
            else
            {
                showWarningForDifferentWHS = true;
            }
        }

        if (showWarningForDifferentWHS)
        {
            warning(strFmt("@WAX:LPWDifferentWarehousesWarning", currentInventLocationId));
        }

        loadPlanningWorkbenchForm.addShipmentToNewLoadInsert(loadTable, shipmentTable);

        for (shipmentTable = whsShipmentTableUnassigned_ds.getFirst(true) ? whsShipmentTableUnassigned_ds.getFirst(true) : whsShipmentTableUnassigned_ds.cursor();
                shipmentTable;
                shipmentTable = whsShipmentTableUnassigned_ds.getNext())
        {
            if (!restrictAddShipmentWhenDiffWHS)
            {
                loadPlanningWorkbenchForm.addShipmentToNewLoadTable(shipmentTable, loadTable);
            }
            else if (shipmentInLoad.in(shipmentTable.ShipmentId))
            {
                loadPlanningWorkbenchForm.addShipmentToNewLoadTable(shipmentTable, loadTable);
            }

        }

        ttscommit;
    }
}

private void assertWeightAndVolumeLimits(WHSLoadTable _load, WHSLoadTemplateId _loadTemplateId)
{
    Weight totalShipmentsWeight = 0;
    Volume totalShipmentsVolume = 0;

    for (WHSShipmentTable shipmentTable = whsShipmentTableUnassigned_ds.getFirst(true) ? whsShipmentTableUnassigned_ds.getFirst(true) : whsShipmentTableUnassigned_ds.cursor();
            shipmentTable;
            shipmentTable = whsShipmentTableUnassigned_ds.getNext())
    {
        totalShipmentsWeight += shipmentTable.displayTotalWeight();
        totalShipmentsVolume += shipmentTable.displayTotalVolume();
    }

    var currentLoadWeight = _load.displayTotalWeight();
    var currentLoadVolume = _load.displayTotalVolume();

    var template = WHSLoadTemplate::find(_loadTemplateId);
    real maxWeight = template.LoadMaxWeight != 0 ? template.LoadMaxWeight : realMax(); // treat 0 as disabled
    real maxVolume = template.LoadMaxVolume != 0 ? template.LoadMaxVolume : realMax(); // treat 0 as disabled

    boolean canAdd = 
        (maxWeight >= currentLoadWeight + totalShipmentsWeight)
        && (maxVolume >= currentLoadVolume + totalShipmentsVolume);

    if (!canAdd)
    {
        // Ask user to override
        canAdd = Box::okCancel("@WAX1811", DialogButton::Ok, "@SYS866") == DialogButton::Ok;
    }

    if (!canAdd)
    {
        throw error("@WAX:WeightOrVolumeExceededCapacity");
    }
}

