/// <summary>
/// Executes the query on the active supply/demand tab.
/// </summary>
/// <param name = "_resetActivePageQueryFlagOnly">Determines whether active page query flag should only be refreshed to the currently active supply/demand tab.</param>
/// <returns>The number of rows loaded by the data source.</returns>
public int executeQueryActivePage(boolean _resetActivePageQueryFlagOnly = false)
{
    int numberOfRows = 0;       

    if (salesLines.isActivePage())
    {
        salesLine_ds.executeQuery();
        numberOfRows = salesLine_ds.numberOfRowsLoaded();
        isSalesLineQuery = false;
    }
    else if (inventLines.isActivePage())
    {            
        inventTransferLine_ds.executeQuery();
        numberOfRows = inventTransferLine_ds.numberOfRowsLoaded();
        isTransferLineQuery = false;
    }
    else if (unassignedShipments.isActivePage())
    {
        whsShipmentTableUnassigned_ds.executeQuery();
        numberOfRows = whsShipmentTableUnassigned_ds.numberOfRowsLoaded();
        isShipmentTableUnassignedQuery = false;
    }
    else if (transportRequestLines.isActivePage())
    {
        tmsTransportRequestLine_ds.executeQuery();
        numberOfRows = whsShipmentTableUnassigned_ds.numberOfRowsLoaded();
        isTransportRequestLineQuery = false;
    }

    if (!_resetActivePageQueryFlagOnly)
    {
        isSalesLineQuery                = !salesLines.isActivePage();
        isTransferLineQuery             = !inventLines.isActivePage();
        isShipmentTableUnassignedQuery  = !unassignedShipments.isActivePage();
        isTransportRequestLineQuery     = !transportRequestLines.isActivePage();
    }

    return numberOfRows;
}

public int researchActivePage(boolean _resetActivePageQueryFlagOnly = false)
{
    int numberOfRows = 0;

    if (salesLines.isActivePage())
    {
        salesLine_ds.research(true);
        numberOfRows = salesLine_ds.numberOfRowsLoaded();
        isSalesLineQuery = false;
    }
    else if (inventLines.isActivePage())
    {
        inventTransferLine_ds.research(true);
        numberOfRows = inventTransferLine_ds.numberOfRowsLoaded();
        isTransferLineQuery = false;
    }
    else if (unassignedShipments.isActivePage())
    {
        whsShipmentTableUnassigned_ds.research(true);
        numberOfRows = whsShipmentTableUnassigned_ds.numberOfRowsLoaded();
        isShipmentTableUnassignedQuery = false;
    }
    else if (transportRequestLines.isActivePage())
    {
        tmsTransportRequestLine_ds.research(true);
        numberOfRows = whsShipmentTableUnassigned_ds.numberOfRowsLoaded();
        isTransportRequestLineQuery = false;
    }

    if (!_resetActivePageQueryFlagOnly)
    {
        isSalesLineQuery                = !salesLines.isActivePage();
        isTransferLineQuery             = !inventLines.isActivePage();
        isShipmentTableUnassignedQuery  = !unassignedShipments.isActivePage();
        isTransportRequestLineQuery     = !transportRequestLines.isActivePage();
    }

    return numberOfRows;
}

protected boolean restrictToSameWarehouseWhenCreatingLoads()
