private void createTmpLoadLinesFromSalesLines(WHSITmpLoadLineCreator _tmpLoadLineCreator)
{
    boolean restrictToSameWarehouseWhenCreatingLoads = this.restrictToSameWarehouseWhenCreatingLoads();

    InventLocationId currentInventLocationId;

    if (WHSOutboundLPWRestrictAddToLoadWhenDiffWHSFlight::instance().isEnabled())
    {
        // current warehouse id takes value from the first sales order line if sales lines will be added to a new load,
        // or value from existing load if records will be added to existing load
        currentInventLocationId = loadPlanningWorkbenchForm.parmExistingLoadInventLocationId() ?
                    loadPlanningWorkbenchForm.parmExistingLoadInventLocationId() :
                    InventDim::find(salesLine.InventDimId).InventLocationId;
    }
    else
    {
        currentInventLocationId = InventDim::find(salesLine.inventDimId).InventLocationId;
    }

    int numberOfProcessedLines;
    SalesLine salesLineRecord;
    boolean showWarningForDifferentWHS;

    for (salesLineRecord = salesLine_ds.getFirst(true) ? salesLine_ds.getFirst(true) : salesLine_ds.cursor();
        salesLineRecord;
        salesLineRecord = salesLine_ds.getNext())
    {
        if (!restrictToSameWarehouseWhenCreatingLoads || InventDim::find(salesLineRecord.inventDimId).InventLocationId == currentInventLocationId)
        {
            _tmpLoadLineCreator.createTmpLoadLinesFromSalesLine(salesLineRecord, numberOfProcessedLines);
            ++numberOfProcessedLines;
        }
        else
        {
            showWarningForDifferentWHS = true;
        }
    }

    if (showWarningForDifferentWHS)
    {
        warning(strFmt("@WAX:LPWDifferentWarehousesWarning", currentInventLocationId));
    }
}

private void createTmpLoadLinesFromTransferLines(WHSITmpLoadLineCreator _tmpLoadLineCreator)
{
    int numberOfProcessedLines;
    InventTransferLine inventTransferLineRecord;

    for (inventTransferLineRecord = inventTransferLine_ds.getFirst(true) ? inventTransferLine_ds.getFirst(true) : inventTransferLine_ds.cursor();
        inventTransferLineRecord;
        inventTransferLineRecord = inventTransferLine_ds.getNext())
    {
        _tmpLoadLineCreator.createTmpLoadLinesFromInventTransferLine(inventTransferLineRecord, numberOfProcessedLines);
        ++numberOfProcessedLines;
    }
}

private void createTmpLoadLinesFromTransportRequestLines(WHSITmpLoadLineCreator _tmpLoadLineCreator)
{
    for (TMSTransportRequestLine transportRequestLineRecord = tmsTransportRequestLine_ds.getFirst(true) ? tmsTransportRequestLine_ds.getFirst(true) : tmsTransportRequestLine_ds.cursor();
    transportRequestLineRecord;
    transportRequestLineRecord = tmsTransportRequestLine_ds.getNext())
    {
        // ensures that the Pays Freight on the associated orders' dlvTerms are in sync
        _tmpLoadLineCreator.createTmpLoadLinesTransportRequestLines(transportRequestLineRecord);
    }
}

private WHSITmpLoadLineCreator initializeTmpLoadLineCreator()
{
    WHSITmpLoadLineCreator tmpLoadLineCreator = loadPlanningWorkbenchForm;

    if (loadPlanningWorkbenchForm.parmAddOrder())
    {
        tmpLoadLineCreator = WHSOncePerOrderTmpLoadLineCreator::newFromTmpLoadLineCreator(tmpLoadLineCreator);
    }

    return tmpLoadLineCreator;
}

protected void createTmpLoadLines()
{
    WHSITmpLoadLineCreator tmpLoadLineCreator = this.initializeTmpLoadLineCreator();

    loadPlanningWorkbenchForm.parmLoadPlanningWorkbenchServerForm().clearGlobalVariables();

    switch (true)
    {
        case salesLines.isActivePage():
            this.createTmpLoadLinesFromSalesLines(tmpLoadLineCreator);
            break;

        case inventLines.isActivePage():
            this.createTmpLoadLinesFromTransferLines(tmpLoadLineCreator);
            break;

        case transportRequestLines.isActivePage():
            if (WHSOutboundLoadPlanWorkbenchTransRequestLinesFlight::instance().isEnabled())
            {
                this.createTmpLoadLinesFromTransportRequestLines(tmpLoadLineCreator);
            }
            break;
    }
}

