/// <summary>
///    Adds filter to load id range
/// </summary>
/// <param name="_orderNum">
///    The sales id or transfer id tied to load
/// </param>
/// <param name="_inventTransType">
/// The inventTransType for the order.
/// </param>
public void filterLoadGrid(WHSOrderNum _orderNum, InventTransType _inventTransType)
{
    boolean foundLoadLineOnSameOrderNotOnLoad;

    if (salesId)
    {
        SalesLine               locSalesLine;
        WHSSalesLine            locWHSSalesLine;

        select firstonly InventTransId, SalesId from locSalesLine
            exists join locWHSSalesLine
            where locSalesLine.SalesId             == salesId
                && locWHSSalesLine.InventTransId   == locSalesLine.InventTransId
                && locWHSSalesLine.QtyLeftToLoad   != 0;

        if (!locSalesLine)
        {
            foundLoadLineOnSameOrderNotOnLoad = WHSLoadPlanningWorkbenchServerForm::existLoadLineByOrderAndTypeNotOnLoad(salesId, InventTransType::Sales);
        }

        this.applyLoadIdFilter(_orderNum, _inventTransType, locSalesLine || foundLoadLineOnSameOrderNotOnLoad);
    }
    else if (transferId)
    {
        InventTransferLine      locInventTransferLine;
        WHSInventTransferLine   locWHSInventTransferLine;

        select firstonly InventTransId, TransferId from locInventTransferLine
            exists join locWHSInventTransferLine
            where locInventTransferLine.TransferId              == transferId
                && locWHSInventTransferLine.InventTransferLine  == locInventTransferLine.RecId
                && locWHSInventTransferLine.QtyLeftToLoad       != 0;

        if (!locInventTransferLine)
        {
            foundLoadLineOnSameOrderNotOnLoad = WHSLoadPlanningWorkbenchServerForm::existLoadLineByOrderAndTypeNotOnLoad(transferId, InventTransType::TransferOrderShip);
        }

        this.applyLoadIdFilter(_orderNum, _inventTransType, locInventTransferLine || foundLoadLineOnSameOrderNotOnLoad);
    }
}

