protected void setFormInternalState()
{
    if (element.args().record())
    {
        Common comm = element.args().record();

        switch (element.args().dataset())
        {
            case tableNum(SalesTable):
                salesId = comm.(fieldNum(SalesTable, SalesId));
                break;

            case tableNum(InventTransferTable):
                transferId = comm.(fieldNum(InventTransferTable, TransferId));
                break;

            case tableNum(WHSLoadTable):
                filterLoadId = comm.(fieldNum(WHSLoadTable, LoadId));
                break;

            case tableNum(SalesLine):
                salesId = comm.(fieldNum(SalesLine, SalesId));
                break;
        }
    }
}

