/// <summary>
///  Re-executes queries for appropriate datasources
/// </summary>
/// <param name = "_filterType">Applied filter type</param>
protected void reExecuteQueries(WHSLPWFilterType _filterType)
{
    switch (_filterType)
    {
        case WHSLPWFilterType::Load:
            whsLoadTable_ds.executeQuery();
            whsLoadLine_ds.executeQuery();
            break;

        case WHSLPWFilterType::SalesOrder:
            salesLine_ds.executeQuery();
            isSalesLineQuery = false;
            break;

        case WHSLPWFilterType::TransferOrder:
            inventTransferLine_ds.executeQuery();
            isTransferLineQuery = false;
            break;

        case WHSLPWFilterType::Shipment:
            whsShipmentTableUnassigned_ds.executeQuery();  
            isShipmentTableUnassignedQuery = false;
            break;
    }
}

public WHSLPWFilterType getFilterType()
{
    WHSLPWFilterType ret = WHSLPWFilterType::Load;

    switch (true)
    {
        case salesLines.isActivePage():
            ret = WHSLPWFilterType::SalesOrder;
            break;

        case inventLines.isActivePage():
            ret = WHSLPWFilterType::TransferOrder;
            break;

        case UnassignedShipments.isActivePage():
            ret = WHSLPWFilterType::Shipment;
            break;
    }

    return ret;
}

public void restoreQuery(WHSLPWFilterType _filterType)
{
    element.updateQueries(_filterType);
    element.reExecuteQueries(_filterType);
}

/// <summary>
/// Updates the form datasource queries.
/// </summary>
/// <param name = "_filterType">A <c>WHSLPWFilterType</c> value used to determine the datasource to update its query.</param>
protected void updateQueries(WHSLPWFilterType _filterType)
{
    switch (_filterType)
    {
        case WHSLPWFilterType::Load:
            if (origQueryLoads)
            {
                whsLoadTable_ds.query(new QueryRun(origQueryLoads).query());
            }
            break;

        case WHSLPWFilterType::SalesOrder:
            if (origQuerySales)
            {
                salesLine_ds.query(new QueryRun(origQuerySales).query());
            }
            break;

        case WHSLPWFilterType::TransferOrder:
            if (origQueryTransfer)
            {
                inventTransferLine_ds.query(new QueryRun(origQueryTransfer).query());
            }
            break;

        case WHSLPWFilterType::Shipment:
            if (origQueryShip)
            {
                whsShipmentTableUnassigned_ds.query(new QueryRun(origQueryShip).query());
            }
            break;
    }
}

protected boolean activatePage()
{
    switch (true)
    {
        case transferId != '':
            inventLines.activatePage();
            return true;

        case salesId != '':
            UnassignedShipments.pageActivated();
            Common shipCount = whsShipmentTableUnassigned_ds.getFirst();
            if (!shipCount)
            {
                salesLines.activatePage();
            }
            return true;

        case supplyDemandTypeSet && this.activatePageByDemandType(supplyDemandType):
            return true;

        default:                 
    }

    return false;
}

public void run()
{
    super();

    boolean pageActivated = this.activatePage();

    if (!pageActivated)
    {
        UnassignedShipments.pageActivated();
    }

    unassignedShipments.visible(TMSParameters::find().PlanByShipment);

    if (LoadLPWFilter.text() != lpwFilterCodeLoads)
    {
        LoadLPWFilter.text(lpwFilterCodeLoads);
        LoadLPWFilter.modified();
    }
}

/// <summary>
/// Activates form elements based on the type of demand. 
/// </summary>
/// <param name = "_supplyDemandType">The type of demand.</param>
/// <returns>true if a sub element has been activated; otherwise, false.</returns>
private boolean activatePageByDemandType(TMSLoadBuildSupplyDemandType _supplyDemandType)
{
    boolean ret;

    switch (_supplyDemandType)
    {
        case TMSLoadBuildSupplyDemandType::Sales:
            salesLines.activatePage();
            ret = true;
            break;

        case TMSLoadBuildSupplyDemandType::TransferIssue:
            InventLines.activatePage();
            ret = true;
            break;

        case TMSLoadBuildSupplyDemandType::Shipment:
            if (TMSParameters::find().PlanByShipment)
            {
                unassignedShipments.pageActivated();
            }
            ret = true;
            break;
    }    
    return ret;
}

private void showWaveButtons(boolean _showButtons)
{
    whsWaveTable.enabled(_showButtons);
}

public boolean unpack(container _packedClass)
{
    boolean     ret;
    int         version;

    version = conPeek(_packedClass, 1);

    switch (version)
    {
        case #CurrentVersion:
            [version, #CurrentList] = _packedClass;
            ret = true;
            break;
    }

    return ret;
}

