/// <summary>
/// Validates that lines being added to the load are not of a different order type then lines currently on the load.
/// </summary>
/// <returns>
/// true if the source lines can be added; otherwise, false.
/// </returns>
protected boolean validateExistingLoadMatchesOrderType()
{
    WHSLoadLine         shipLoadLine;
    InventTransType     inventTransType;
    WHSShipmentTable    shipmentTable;
    boolean             mustCheck       = true;
    boolean             ret             = true;

    switch (true)
    {
        case salesLines.isActivePage():
            inventTransType = InventTransType::Sales;
            break;

        case inventLines.isActivePage():
            inventTransType = InventTransType::TransferOrderShip;
            break;

        case unassignedShipments.isActivePage():
            shipmentTable = whsShipmentTableUnassigned_ds.getFirst(true) ? whsShipmentTableUnassigned_ds.getFirst(true) : whsShipmentTableUnassigned_ds.cursor();
            select firstonly InventTransType from shipLoadLine
                where shipLoadLine.ShipmentId == shipmentTable.ShipmentId;
            inventTransType = shipLoadLine.InventTransType;
            break;

        default:
            mustCheck = false;
    }

    if (mustCheck)
    {
        ret = WHSLoadTable::validateInventTransTypeMatches(whsLoadTable.LoadId, inventTransType);
    }

    return ret;
}

private void validateShippedLoadLines()
{
    loadPlanningWorkbenchForm.validateShippedLoadLines(salesLines.isActivePage(), salesLine_ds);
}

