protected void setValuesBasedOnFilterTable(WHSLoadPlanningWorkbenchFilter _filterTable)
{
    switch (_filterTable.FilterType)
    {
        case WHSLPWFilterType::SalesOrder:
            lpwFilterCodeSales = _filterTable.FilterCode;
            salesLine_ds.query(new QueryRun(_filterTable.FilterQuery).query());
            break;

        case WHSLPWFilterType::TransferOrder:
            lpwFilterCodeTransfer = _filterTable.FilterCode;
            inventTransferLine_ds.query(new QueryRun(_filterTable.FilterQuery).query());
            break;

        case WHSLPWFilterType::Shipment:
            lpwFilterCodeShip = _filterTable.FilterCode;
            whsShipmentTableUnassigned_ds.query(new QueryRun(_filterTable.FilterQuery).query());
            break;
    }
}

protected void restoreQueryOnFilterModifiedWithBlankFilter(WHSLPWFilterType _filterType)
{
    switch (_filterType)
    {
        case WHSLPWFilterType::SalesOrder:
            lpwFilterCodeSales = '';
            element.restoreQuery(_filterType);
            break;

        case WHSLPWFilterType::TransferOrder:
            lpwFilterCodeTransfer = '';
            element.restoreQuery(_filterType);
            break;

        case WHSLPWFilterType::Shipment:
            lpwFilterCodeShip = '';
            element.restoreQuery(_filterType);
            break;
    }
}

