public void executeQuery()
{
    if (salesId)
    {
        SysQuery::findOrCreateRange(salesLine_ds.query().dataSourceTable(tableNum(SalesLine)), fieldNum(SalesLine, SalesId)).value(queryValue(salesId));
    }

    if (DirectFilters.enabled())
    {
        element.applySiteWHFilters(salesLine_ds);

        loadPlanningWorkbenchForm.applyShippedDateFilter(salesLine_ds.query().dataSourceTable(tableNum(SalesLine)), ShipDateFilter.dateValue());
        loadPlanningWorkbenchForm.applyReceiptDateFilter(salesLine_ds.query().dataSourceTable(tableNum(SalesLine)), ReceiptDateFilter.dateValue());
    }

    super();
}

