[SysClientCacheDataMethodAttribute(true)]
public display Weight displayOutboundWeight(SalesLine _salesLine)
{
    ItemGrossWeight grossWeight = InventTable::find(_salesLine.ItemId).grossWeight();

    return grossWeight == 0 ? 0 : grossWeight * this.displayOutboundQtyLeftToLoad(_salesLine);
}

[SysClientCacheDataMethodAttribute(true)]
public display Volume displayOutboundVolume(SalesLine _salesLine)
{
    Volume unitVolume = InventTable::find(_salesLine.ItemId).UnitVolume;

    return unitVolume == 0 ? 0 : unitVolume * this.displayOutboundQtyLeftToLoad(_salesLine);
}

