[SysClientCacheDataMethodAttribute(true)]
public display WHSQtyLeftToLoad displayOutboundQty(InventTransferLine _inventTransferLine)
{
    return WHSLoadPlanningWorkbenchForm::displayOutboundQty(_inventTransferLine.ItemId, _inventTransferLine.InventTransId);
}

[SysClientCacheDataMethodAttribute(true)]
public display WHSQtyLeftToLoad displayOutboundCWQty(InventTransferLine _inventTransferLine)
{
    return WHSLoadPlanningWorkbenchForm::displayOutboundCWQty(_inventTransferLine.ItemId, _inventTransferLine.InventTransId);
}

[SysClientCacheDataMethodAttribute(true)]
public display Weight displayOutboundWeight(InventTransferLine _inventTransferLine)
{
    ItemGrossWeight grossWeight = InventTable::find(_inventTransferLine.ItemId).grossWeight();

    return grossWeight == 0 ? 0 : grossWeight * this.displayOutboundQtyLeftToLoad(_inventTransferLine);
}

[SysClientCacheDataMethodAttribute(true)]
public display Volume displayOutboundVolume(InventTransferLine _inventTransferLine)
{
    Volume unitVolume = InventTable::find(_inventTransferLine.ItemId).UnitVolume;

    return unitVolume == 0 ? 0 : unitVolume * this.displayOutboundQtyLeftToLoad(_inventTransferLine);
}

