public void init()
{
    QueryBuildDataSource    qbds;

    super();

    element.updateDesign_Transport(InventDimFormDesignUpdate::Init);

    qbds            = this.query().dataSourceTable(tableNum(TMSTransportRequestLine));

    qbrTransport    = qbds.addRange(fieldNum(TMSTransportRequestLine, QtyLeftToTransport));
    qbrTransportId  = qbds.addRange(fieldNum(TMSTransportRequestLine, OrderNum));

    qbrTransport.status(RangeStatus::Hidden);
    qbrTransport.value('>0');
}

public void executeQuery()
{
    qbrTransport.value('>0');

    qbrTransportId.value(loadPlanningWorkbenchForm.transportRequestLine_executeQuery(salesId, '', transferId));

    if (DirectFilters.enabled())
    {
        element.applySiteWHFilters(TMSTransportRequestLine_ds);
    }

    super();
}

