public boolean modified()
{
    boolean ret = super();

    WHSLPWFilterCode filterCode = this.valueStr();

    if (filterCode)
    {
        WHSLoadPlanningWorkbenchFilter filterTable = WHSLoadPlanningWorkbenchFilter::find(filterCode, WHSLPWFilterType::Load);

        if (filterTable)
        {
            lpwFilterCodeLoads = filterTable.FilterCode;
            whsLoadTable_ds.query(new QueryRun(filterTable.FilterQuery).query());

            element.reExecuteQueries(WHSLPWFilterType::Load);
        }
        else
        {
            ret = false;
        }
    }
    else
    {
        lpwFilterCodeLoads = '';
        element.restoreQuery(WHSLPWFilterType::Load);
    }

    return ret;
}

public boolean modified()
{
    boolean ret = super();

    hideShippedOrders = hideShipped.checked();
    whsLoadTable_ds.executeQuery();

    if (!hideShippedOrders)
    {
        hideShippedOrdersMessageId = Message::Add(MessageSeverity::Informational, "@WAX:WHSLoadPlanningWorkbenchHideShippedPerformanceInfo");
    }
    else if (hideShippedOrdersMessageId)
    {
        Message::Remove(hideShippedOrdersMessageId);
        hideShippedOrdersMessageId = 0;
    }

    return ret;
}

public boolean modified()
{
    if (whsLoadTable.orig().CarrierCode != this.text())
    {
        whsLoadTable.CarrierServiceCode = '';
    }

    return super();
}

