[Form]
public class WHSRFMenuItem extends FormRun implements WHSIQueryPartCallback
{
    private const str WHSRFMenuItemQueryPart_Range = menuItemDisplayStr(WHSRFMenuItemQueryPart_Range);
    private const str WHSRFMenuItemQueryPart_Sorting = menuItemDisplayStr(WHSRFMenuItemQueryPart_Sorting);
    private WHSIQueryPart headerRangePart;
    private WHSIQueryPart headerSortingPart;
    private WHSRFMenuItemActiveQuery whsRFMenuItemActiveQuery;

    WHSRFMenuItemForm whsrfMenuItemForm;

    [SysObsolete('Use the packed queries from the data source instead.', false, 30\06\2020)]
    QueryRun          queryRun;

}

private void initWorkListQueryFormParts()
{
    str fieldNameQuery = fieldId2Name(tableNum(whsrfMenuItemTable), fieldNum(whsrfMenuItemTable, WorkListQuery));
    str fieldNameQueryName = fieldId2Name(tableNum(whsrfMenuItemTable), fieldNum(whsrfMenuItemTable, MenuItemName));
    headerRangePart = WHSQueryHelper::initializeQueryFormPart(element, WHSRFMenuItemQueryPart_Range, fieldNameQuery, '%1', fieldNameQueryName, whsrfMenuItemTable.defaultWorkListQueryRun(), true, false);
    headerSortingPart = WHSQueryHelper::initializeQueryFormPart(element, WHSRFMenuItemQueryPart_Sorting, fieldNameQuery, '%1', fieldNameQueryName, whsrfMenuItemTable.defaultWorkListQueryRun(), false, true);
    headerRangePart.refresh();
    headerSortingPart.refresh();
    whsRFMenuItemActiveQuery = WHSRFMenuItemActiveQuery::WorkList;
}

private void initDataInquiryQueryFormParts()
{
    str fieldNameQuery = fieldId2Name(tableNum(whsrfMenuItemTable), fieldNum(whsrfMenuItemTable, DataInquiryQuery));
    str fieldNameQueryName = fieldId2Name(tableNum(whsrfMenuItemTable), fieldNum(whsrfMenuItemTable, MenuItemName));
    headerRangePart = WHSQueryHelper::initializeQueryFormPart(element, WHSRFMenuItemQueryPart_Range, fieldNameQuery, '%1', fieldNameQueryName, whsrfMenuItemTable.defaultDataInquiryQueryRun(), true, false);
    headerSortingPart = WHSQueryHelper::initializeQueryFormPart(element, WHSRFMenuItemQueryPart_Sorting, fieldNameQuery, '%1', fieldNameQueryName, whsrfMenuItemTable.defaultDataInquiryQueryRun(), false, true);
    headerRangePart.refresh();
    headerSortingPart.refresh();
    whsRFMenuItemActiveQuery = WHSRFMenuItemActiveQuery::DataInquiry;
}

private void initReprintMultipleCartonQueryFormParts()
{
    str fieldNameQuery = fieldId2Name(tableNum(whsrfMenuItemTable), fieldNum(whsrfMenuItemTable, CartonGroupQuery));
    str fieldNameQueryName = fieldId2Name(tableNum(whsrfMenuItemTable), fieldNum(whsrfMenuItemTable, MenuItemName));
    headerRangePart = WHSQueryHelper::initializeQueryFormPart(element, WHSRFMenuItemQueryPart_Range, fieldNameQuery, '%1', fieldNameQueryName, whsrfMenuItemTable.defaultCartonGroupQueryRun(), true, false);
    headerSortingPart = WHSQueryHelper::initializeQueryFormPart(element, WHSRFMenuItemQueryPart_Sorting, fieldNameQuery, '%1', fieldNameQueryName, whsrfMenuItemTable.defaultCartonGroupQueryRun(), false, true);
    headerRangePart.refresh();
    headerSortingPart.refresh();
    whsRFMenuItemActiveQuery = WHSRFMenuItemActiveQuery::ReprintMultipleCarton;
}

public void queryUpdated(FormRun _sender, container _packedQuery)
{
    switch (whsRFMenuItemActiveQuery)
    {
        case WHSRFMenuItemActiveQuery::WorkList:
            whsrfMenuItemTable.WorkListQuery = _packedQuery;
            break;
        case WHSRFMenuItemActiveQuery::DataInquiry:
            whsrfMenuItemTable.DataInquiryQuery = _packedQuery;
            break;
        case WHSRFMenuItemActiveQuery::ReprintMultipleCarton:
            whsrfMenuItemTable.CartonGroupQuery = _packedQuery;
            break;
    }

    whsrfMenuItemTable.write();

    headerRangePart.refresh();
    headerSortingPart.refresh();
}

public void init()
{
    whsrfMenuItemForm = new WHSRFMenuItemForm();

    super();

    whsTmpFieldName.populateFieldNames(tableNum(WHSWorkTable), true);

    boolean enableSysDirectedWorkSequenceQuery = true;

    whsrfSysDirSortButton.visible(!enableSysDirectedWorkSequenceQuery);
    WHSRFSystemDirectedWorkSequenceQuery.visible(enableSysDirectedWorkSequenceQuery);
}

private void toggleDataInquiryButtonsEnabledState()
{
    EditGenericDataInquiryQuery.enabled(whsrfMenuItemTable.isButtonEnabledDataInquiryQuery());
    WHSGenericDataInquiryFieldList.enabled(EditGenericDataInquiryQuery.enabled() || whsrfMenuItemTable.isButtonEnabledDataInquiryQuery());
}

/// <summary>
/// Updates the visibility and enable state of form controls
/// </summary>
/// <remarks>Wrap the getControlsVisibilityProperties method when adding new controls. Wrap the isFieldVisible method on the table when changing existing controls.</remarks>
public void toggleFields()
{
    this.setFieldsVisibility();

    // Dependent controls
    EditWorkListQuery.enabled(whsrfMenuItemTable.isFieldEnabledWorkListQuery());
    WHSWorkListFieldList.enabled(EditWorkListQuery.enabled() || whsrfMenuItemTable.isFieldEnabledWorkLineListQuery());
    whsrfDefaultDataButton.enabled(whsrfMenuItemTable.DefaultData);
    whsrfMenuItemTable_UseExistingWork.enabled(whsrfMenuItemTable.isFieldEnabledUseExistingWork());
    workClass.visible(whsrfMenuItemTable.isFieldVisibleWorkClass());
    WHSRFMenuItemTable_ProcessGuideUseFramework.enabled(whsrfMenuItemTable.isFieldEnabledUseProcessGuide());

    this.toggleDataInquiryButtonsEnabledState();

    if (whsrfMenuItemTable_RFSysGroupField.visible())
    {
        if (whsrfMenuItemTable.MenuItemDirectedBy == WHSMenuItemDirectedBy::ValidatedUserDirected)
        {
            whsrfMenuItemTable_RFSysGroupField.label("@WAX2330");
            whsrfMenuItemTable_RFSysGroupLabel.label("@WAX2329");
        }
        else
        {
            whsrfMenuItemTable_RFSysGroupField.label("@WAX1926");
            whsrfMenuItemTable_RFSysGroupLabel.label("@WAX1937");
        }
    }

    if (WHSRFMenuItemTable_UseExistingCatchWeightTag.visible())
    {
        WHSRFMenuItemTable_UseExistingCatchWeightTag.enabled(whsRFMenuItemTable.isFieldEnabledUseExistingCatchWeightTag());
    }

    //Unbound controls

    whsrfMenuItemCycleCountButton.enabled(whsrfMenuItemTable.WorkCreationProcess == WHSWorkCreationProcess::SpotCycleCounting
                                          || whsrFmenuItemTable.UseExistingWork);

    whsrfSysDirSortButton.enabled(whsrfMenuItemTable.MenuItemDirectedBy == WHSMenuItemDirectedBy::SystemDirected);
    WHSRFSystemDirectedWorkSequenceQuery.enabled(whsrfMenuItemTable.MenuItemDirectedBy == WHSMenuItemDirectedBy::SystemDirected
                                                    || whsrfMenuItemTable.MenuItemDirectedBy == WHSMenuItemDirectedBy::SystemDirectedClusterPicking);

    whsrfAutoConfirmButton.enabled(whsrfMenuItemTable.isFieldEnabledWorkAutoConfirm());

    whsrfMenuItemForm.enableEditReasonCodeControl(WHSRFMenuItemTable_EditCountingReasonCode, whsrfMenuItemTable);

    if (whsrfMenuItemTable.WorkCreationProcess == WHSWorkCreationProcess::Movement)
    {
        WHSRFMenuItemTable_DeferredPutProcessingPolicy.helpText("@FieldDescriptions_WHS:WHSDeferredOperationProcessingMobileDevicePolicy_helpText");
        WHSRFMenuItemTable_DeferredPutProcessingPolicy.label("@WAX:WHSDeferredOperationProcessingMobileDevicePolicy_FieldLabel");

        this.overridePolicyEnumLabel("@WAX:WHSDeferredPutProcessingMobileDevicePolicy_IgnoreSettings", "@WAX:WHSDeferredOperationProcessingMobileDevicePolicy_OverrideSettings_Label");
    }
    else
    {
        WHSRFMenuItemTable_DeferredPutProcessingPolicy.helpText("@FieldDescriptions_WHS:WHSDeferredPutProcessingMobileDevicePolicy_helpText");
        WHSRFMenuItemTable_DeferredPutProcessingPolicy.label("@WAX:WHSDeferredPutProcessingMobileDevicePolicyEnum");

        this.overridePolicyEnumLabel("@WAX:WHSDeferredOperationProcessingMobileDevicePolicy_OverrideSettings_Label", "@WAX:WHSDeferredPutProcessingMobileDevicePolicy_IgnoreSettings");
    }

    if(EditWorkListQuery.visible())
    {
        if(WHSRFMenuItemTable.WorkActivity == WHSWorkActivity::WorkList)
        {
            this.initWorkListQueryFormParts();
        }
        else if(WHSRFMenuItemTable.WorkActivity == WHSWorkActivity::ReprintMultipleCartonLabels)
        {
            this.initReprintMultipleCartonQueryFormParts();
        }
    }
    else if(EditGenericDataInquiryQuery.visible())
    {
        this.initDataInquiryQueryFormParts();
    }


    WHSRFMenuItemTable_ds.object(fieldNum(WHSRFMenuItemTable, ReturnItemReceivingPolicyId)).mandatory(WHSRFMenuItemTable.isFieldVisibleReturnItemReceivingPolicyId());
}

private void overridePolicyEnumLabel(str _oldLabel, str _newLabel)
{
    for (int i = 1; i <= WHSRFMenuItemTable_DeferredPutProcessingPolicy.count(); i++)
    {
        WHSRFMenuItemTable_DeferredPutProcessingPolicy.item(i);

        if (WHSRFMenuItemTable_DeferredPutProcessingPolicy.text() == _oldLabel)
        {
            WHSRFMenuItemTable_DeferredPutProcessingPolicy.text(_newLabel);
            break;
        }
    }
}

/// <summary>
/// Updates the fields visibility
/// </summary>
internal void setFieldsVisibility()
{
    Map observableControls = this.getControlsVisibilityProperties();
    MapEnumerator en = observableControls.getEnumerator();

    while (en.moveNext())
    {
        FormControl formControl = en.currentKey();

        if (formControl)
        {
            en.currentKey().visible(en.currentValue());
        }
    }
}

