/// <summary>
/// Marking by "true/false" table fields in collection according to conditions whether they should be visible on the form
/// </summary>
/// <param name = "_rfMenuItemTable">
/// reference to <c>WHSRFMenuItemTable</c> where fields visibility conditions have to be analyzed
/// </param>
/// <returns> map of data fields, those visibility depends on other fields values</returns>
internal Map getFieldsVisibilityProperties(WHSRFMenuItemTable _rfMenuItemTable = whsrfMenuItemTable)
{
    Map observableFields = new Map(Types::Integer, Types::Enum);           
    MapEnumerator en = this.getControlsVisibilityProperties(_rfMenuItemTable).getEnumerator();

    while (en.moveNext())
    {
        FieldId fieldId = en.currentKey().fieldBinding().fieldId();

        if (fieldId)
        {
            observableFields.insert(fieldId, en.currentValue());
        }
    }

    return observableFields;
}

