public void clicked()
{
    super();

    SysQueryRun sysQueryRun;

    switch (WHSRFMenuItemTable.WorkActivity)
    {
        case WHSWorkActivity::WorkList:
            sysQueryRun = WHSFormHelper::initializeQueryRunForEditing(
                whsrfMenuItemTable.WorkListQuery,
                whsrfMenuItemTable.MenuItemName,
                whsrfMenuItemTable.defaultWorkListQueryRun());

            sysQueryRun.initEditQueryFormParameter(WHSRFMenuItemTable);

            if (sysQueryRun.prompt())
            {
                whsrfMenuItemTable.WorkListQuery = sysQueryRun.pack();                       
                whsrfMenuItemTable.write();

                if (headerRangePart)
                {
                    headerRangePart.refresh();
                }

                if (headerSortingPart)
                {
                    headerSortingPart.refresh();
                }

            }

            // For backwards compatibility
            queryRun = new QueryRun(whsrfMenuItemTable.WorkListQuery);
            break;

        case WHSWorkActivity::ReprintMultipleCartonLabels:
            sysQueryRun = WHSFormHelper::initializeQueryRunForEditing(
                whsrfMenuItemTable.CartonGroupQuery,
                whsrfMenuItemTable.MenuItemName,
                whsrfMenuItemTable.defaultCartonGroupQueryRun());

            sysQueryRun.initEditQueryFormParameter(WHSRFMenuItemTable);

            if (sysQueryRun.prompt())
            {
                whsrfMenuItemTable.CartonGroupQuery = sysQueryRun.pack();
                whsrfMenuItemTable.write();

                if (headerRangePart)
                {
                    headerRangePart.refresh();
                }

                if (headerSortingPart)
                {
                    headerSortingPart.refresh();
                }

            }

            // For backwards compatibility
            queryRun = new QueryRun(whsrfMenuItemTable.CartonGroupQuery);
            break;
    }
}

public void clicked()
{
    super();

    if (whsrfMenuItemTable.DataInquiryQuery)
    {
        var sysQueryRun = WHSFormHelper::initializeQueryRunForEditing(
                whsrfMenuItemTable.DataInquiryQuery,
                whsrfMenuItemTable.MenuItemName,
                whsrfMenuItemTable.defaultDataInquiryQueryRun());

        sysQueryRun.initEditQueryFormParameter(WHSRFMenuItemTable);

        if (sysQueryRun.prompt())
        {
            whsrfMenuItemTable.DataInquiryQuery = sysQueryRun.pack();                    
            whsrfMenuItemTable.write();

            if (headerRangePart)
            {
                headerRangePart.refresh();
            }

            if (headerSortingPart)
            {
                headerSortingPart.refresh();
            }

        }
    }
    else
    {
        error("@WAX:WHSQueryNotSpecifiedError");
    }
}

