[Form]
[SysObsolete('The form is obsolete as it does not work with batches correctly, use form WHSReleaseToWarehouseProdBOMV2 instead.', false, 01\03\2023)]
public class WHSReleaseToWarehouseProdBOM extends FormRun  implements SysIFilterConsumerForm
{
    QueryBuildRange                  qbrReleaseToWarehouseId;
    WHsReleaseToWarehouseProdBOMForm whsReleaseToWarehouseForm;
    InventDimCtrl_Frm                inventDimFormSetupProdBOM;

}

public void registerDatasourceOnQueryingEvent()
{
    WHSInventTransSumDimProd_ds.OnQueryExecuting += eventhandler(this.parmFilterFormQueryEventHandler().applyFilter);
}

public void enableButtonReleaseToWarehouse()
{        
    boolean canBeReleasedToWarehouse = prodBOM.canBeReleasedToWarehouse();

    ButtonAdd_Productions.enabled(canBeReleasedToWarehouse);
    AddOrder_Productions.enabled(canBeReleasedToWarehouse);
}

public void close()
{
    whsReleaseToWarehouseForm.removeRelease(whsTmpReleaseToWarehouse_ds, whsTmpReleaseToWarehouse, false);

    super();
}

public display WHSTotalOrderLines displayTotalOrderLines()
{
    return whsReleaseToWarehouseForm.parmTotalOrderLines();
}

public display WHSTotalOrders displayTotalOrders()
{
    return whsReleaseToWarehouseForm.parmTotalOrders();
}

public void init()
{
    whsReleaseToWarehouseForm = new WHsReleaseToWarehouseProdBOMForm();

    super();

    whsReleaseToWarehouseForm.parmReleaseToWarehouseId(WHSTmpReleaseToWarehouse::getNextReleaseToWarehouseId());
}

/// <summary>
/// Returns the dimensions display object.
/// </summary>
/// <returns>
/// The dimensions display object
/// </returns>
public InventDimCtrl_Frm inventDimSetupObject()
{
    return inventDimFormSetupProdBOM;
}

/// <summary>
/// Updates the dimensions display object for the produciton grid.
/// </summary>
/// <param name="_mode">
/// The state of the dimensions display form.
/// </param>
/// <exception cref="Exception::Error">
/// Incorrect parameter for the function.
/// </exception>
public void updateDesign_ProdBOM(InventDimFormDesignUpdate _mode)
{
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetupProdBOM)
            {
                inventDimFormSetupProdBOM = InventDimCtrl::newFromFormValidDataSource(element, inventDimProd_ds.name());
            }
        // do continue
        case InventDimFormDesignUpdate::Active:
            inventDimFormSetupProdBOM.formActiveSetup();
            inventDimFormSetupProdBOM.formSetControls(true);
            break;

        default:
            throw error(strFmt("@SYS54195", funcName()));
    }
}

private void filterOnWHSEnabledWarehousesAndBlankWarehouses(QueryBuildDataSource _queryBuildDataSource)
{
    QueryBuildDataSource qbdsInventLocation = _queryBuildDataSource.addDataSource(tableNum(InventLocation));
    qbdsInventLocation.joinMode(JoinMode::ExistsJoin);
    qbdsInventLocation.addRange(fieldNum(InventLocation, RecId)).value(
            strFmt('((%1.%2 == %3) || ((%1.%2 == %4.%5) && (%4.%6 == NoYes::Yes)))',
                _queryBuildDataSource.name(),
                fieldStr(InventDim, InventLocationId),
                SysQuery::valueEmptyString(),
                qbdsInventLocation.name(),
                fieldStr(InventLocation, InventLocationId),
                fieldStr(InventLocation, WHSEnabled)));
}

public void research(boolean _retainPosition = false)
{
    whsReleaseToWarehouseForm.calculateTotals(whsTmpReleaseToWarehouse);

    super(_retainPosition);
}

public void executeQuery()
{
    qbrReleaseToWarehouseId.value(whsReleaseToWarehouseForm.parmReleaseToWarehouseId());

    whsReleaseToWarehouseForm.calculateTotals(whsTmpReleaseToWarehouse);

    super();
}

public void init()
{
    super();

    qbrReleaseToWarehouseId = whsTmpReleaseToWarehouse_ds.query().dataSourceTable(tableNum(WHSTmpReleaseToWarehouse))
                                                                 .addRange(fieldNum(WHSTmpReleaseToWarehouse, ReleaseToWarehouseId));
    qbrReleaseToWarehouseId.status(2);
}

public void init()
{
    super();

    element.updateDesign_ProdBOM(InventDimFormDesignUpdate::Init);
}

public int active()
{
    int ret = super();

    element.updateDesign_ProdBOM(InventDimFormDesignUpdate::Active);

    return ret;
}

public void init()
{
    super();

    QueryBuildDataSource prodBOMQbds = whsInventTransSumDimProd_q.dataSourceTable(tableNum(ProdBOM));
    prodBOMQbds.addSortField(fieldNum(ProdBOM, ProdId));
    prodBOMQbds.addSortField(fieldNum(ProdBOM, OprNum));

    QueryBuildDataSource qbdsWHSInventEnabled = prodBOMQbds.addDataSource(tableNum(WHSInventEnabled));
    qbdsWHSInventEnabled.joinMode(JoinMode::ExistsJoin);
    qbdsWHSInventEnabled.addLink(fieldNum(ProdBOM, ItemId), fieldNum(WHSInventEnabled, ItemId), ProdBOM_ds.name());

    QueryBuildDataSource qbdsProdRoute = prodBOMQbds.addDataSource(tableNum(ProdRoute));

    qbdsProdRoute.joinMode(JoinMode::ExistsJoin);

    qbdsProdRoute.addLink(fieldNum(ProdBOM, ProdId), fieldNum(ProdRoute, ProdId), ProdBOM_ds.name());
    qbdsProdRoute.addLink(fieldNum(ProdBOM, OprNum), fieldNum(ProdRoute, OprNum), ProdBOM_ds.name());

    this.cacheAddMethod(tableMethodStr(ProdBOM, displayInputWMSLocationId));
    this.cacheAddMethod(tableMethodStr(ProdBOM, displayRemainingToReleaseQty));
    this.cacheAddMethod(tableMethodStr(ProdBOM, displayReleasedQty));
    this.cacheAddMethod(tableMethodStr(ProdBOM, availPhysical));
}

public int active()
{
    int ret = super();

    element.enableButtonReleaseToWarehouse();

    return ret;
}

public void init()
{
    super();

    element.filterOnWHSEnabledWarehousesAndBlankWarehouses(this.queryBuildDataSource());
}

public void init()
{
    super();

    QueryBuildDataSource prodTableQbds = whsInventTransSumDimProd_q.dataSourceTable(tableNum(ProdTable));
    prodTableQbds.clearLinks();
    prodTableQbds.clearRanges();
    prodTableQbds.addLink(fieldNum(ProdBOM, ProdId), fieldNum(ProdTable, ProdId), ProdBOM_ds.name());

    QueryBuildRange prodStatusRange = prodTableQbds.addRange(fieldNum(ProdTable, ProdStatus));
    prodStatusRange.value(SysQuery::range(ProdStatus::Released, ProdStatus::ReportedFinished));
}

public void clicked()
{
    super();

    MultiSelectionHelper multiSelectHelper = MultiSelectionHelper::construct();
    multiSelectHelper.parmDatasource(whsInventTransSumDimProd_ds);

    WHSInventTransSumDimIssue inventTransSumDim = multiSelectHelper.getFirst();
    while (inventTransSumDim.RecId != 0)
    {
        whsReleaseToWarehouseForm.addInventSumDimFromTransIdDimensionHandlingQty(inventTransSumDim.InventTransId,
                                                                    inventTransSumDim.InventDimId,
                                                                    inventTransSumDim.ItemId,
                                                                    ProdBOM::findTransId(inventTransSumDim.InventTransId).displayRemainingToReleaseQty(),
                                                                    whsTmpReleaseToWarehouse,
                                                                    WHSModule::ProdBOM);

        inventTransSumDim = multiSelectHelper.getNext();
    }

    whsInventTransSumDimProd_ds.executeQuery();
    whsTmpReleaseToWarehouse_ds.executeQuery();
}

public void clicked()
{
    super();

    whsReleaseToWarehouseForm.addProdOrder(prodBOM.ProdId, whsTmpReleaseToWarehouse);

    whsInventTransSumDimProd_ds.executeQuery();
    whsTmpReleaseToWarehouse_ds.executeQuery();
}

public void clicked()
{
    whsReleaseToWarehouseForm.removeRelease(whsTmpReleaseToWarehouse_ds, whsTmpReleaseToWarehouse, true);

    whsTmpReleaseToWarehouse_ds.executeQuery();
}

public void clicked()
{
    whsReleaseToWarehouseForm.removeRelease(whsTmpReleaseToWarehouse_ds, whsTmpReleaseToWarehouse, false);

    whsTmpReleaseToWarehouse_ds.executeQuery();
}

public void clicked()
{
    super();

    whsReleaseToWarehouseForm.prodBOMbuttonRelease_clicked(whsTmpReleaseToWarehouse);

    WHSInventTransSumDimProd_ds.research(true);
}
