[Form]
public class WHSReplenishmentTemplates extends FormRun implements WHSIWorkTemplateFormFilter, WHSIQueryPartCallback
{
    private const str WHSReplenishmentTemplatesProductsQueryPart_Range = menuItemDisplayStr(WHSReplenishmentTemplatesProductsQueryPart_Range);
    private const str WHSReplenishmentTemplatesProductVariantsQueryPart_Range = menuItemDisplayStr(WHSReplenishmentTemplatesProductVariantsQueryPart_Range);
    private const str WHSReplenishmentTemplatesLocationsToReplenishQueryPart_Range = menuItemDisplayStr(WHSReplenishmentTemplatesLocationsToReplenishQueryPart_Range);
    private const str WHSReplenishmentTemplatesZonesToReplenishQueryPart_Range = menuItemDisplayStr(WHSReplenishmentTemplatesZonesToReplenishQueryPart_Range);
    private WHSSequenceNumberMoverForm whsSequenceNumberMoverForm;
    private WHSIQueryPart productsRangePart;
    private WHSIQueryPart productVariantRangePart;
    private WHSIQueryPart loc2ReplRangePart;
    private WHSIQueryPart zone2ReplRangePart;

}

public void init()
{
    super();


    this.initQueryFormParts();


    if (WHSReplenishmentTemplateFormSeqNumButtonsFlight::instance().isEnabled())
    {
        whsSequenceNumberMoverForm = WHSSequenceNumberMoverForm::newFromDataSource(
            WHSReplenishmentTemplateLine_ds,
            fieldStr(WHSReplenishmentTemplateLine, SeqNum),
            [fieldStr(WHSReplenishmentTemplateLine, ReplenishTemplateId)]);

        whsSequenceNumberMoverForm.initNewButtons(ButtonGroup);
    }
}

private void initQueryFormParts()
{
    QueryRun productsQueryRun;
    if (WHSReplenishmentTemplateLine.ItemQuery == conNull())
    {
        productsQueryRun = new QueryRun(WHSReplenishmentTemplateLine.initItemQuery());
    }
    else
    {
        productsQueryRun = new QueryRun(whsReplenishmentTemplateLine.ItemQuery);
    }
    str productsFieldNameQuery = fieldId2Name(tableNum(whsReplenishmentTemplateLine), fieldNum(whsReplenishmentTemplateLine, ItemQuery));
    productsRangePart = WHSQueryHelper::initializeQueryFormPart(element, WHSReplenishmentTemplatesProductsQueryPart_Range, productsFieldNameQuery, null, null, productsQueryRun, true, false);

    QueryRun productVariantQueryRun;
    if (whsReplenishmentTemplateLine.ProductVariantQuery == conNull())
    {
        productVariantQueryRun = new QueryRun(WHSReplenishmentTemplateLine.initProductVariantQuery());
    }
    else
    {
        productVariantQueryRun = new QueryRun(whsReplenishmentTemplateLine.ProductVariantQuery);
    }
    str productVariantFieldNameQuery = fieldId2Name(tableNum(whsReplenishmentTemplateLine), fieldNum(whsReplenishmentTemplateLine, ProductVariantQuery));
    productVariantRangePart = WHSQueryHelper::initializeQueryFormPart(element, WHSReplenishmentTemplatesProductVariantsQueryPart_Range, productVariantFieldNameQuery, null, null, productVariantQueryRun, true, false, null);

    QueryRun loc2ReplQueryRun;
    if (whsReplenishmentTemplateLine.LocationQuery == conNull())
    {
        loc2ReplQueryRun = new QueryRun(queryStr(WHSWMSLocation));
    }
    else
    {
        loc2ReplQueryRun = new QueryRun(whsReplenishmentTemplateLine.LocationQuery);
    }
    str loc2ReplFieldNameQuery = fieldId2Name(tableNum(whsReplenishmentTemplateLine), fieldNum(whsReplenishmentTemplateLine, LocationQuery));
    loc2ReplRangePart = WHSQueryHelper::initializeQueryFormPart(element, WHSReplenishmentTemplatesLocationsToReplenishQueryPart_Range, loc2ReplFieldNameQuery, null, null, loc2ReplQueryRun, true, false, null);

    QueryRun zone2ReplQueryRun;
    if (whsReplenishmentTemplateLine.ZoneQuery == conNull())
    {
        zone2ReplQueryRun = new QueryRun(queryStr(WHSZoneQuery));
    }
    else
    {
        zone2ReplQueryRun = new QueryRun(whsReplenishmentTemplateLine.ZoneQuery);
    }
    str zone2ReplFieldNameQuery = fieldId2Name(tableNum(whsReplenishmentTemplateLine), fieldNum(whsReplenishmentTemplateLine, ZoneQuery));
    zone2ReplRangePart = WHSQueryHelper::initializeQueryFormPart(element, WHSReplenishmentTemplatesZonesToReplenishQueryPart_Range, zone2ReplFieldNameQuery, null, null, zone2ReplQueryRun, true, false, null);
}

public void queryUpdated(FormRun _sender, container _packedQuery)
{
    str menuItemName = _sender.args().menuItemName();

    if(menuItemName == WHSReplenishmentTemplatesProductsQueryPart_Range)
    {
        whsReplenishmentTemplateLine.ItemQuery = _packedQuery;
    }
    else if(menuItemName == WHSReplenishmentTemplatesProductVariantsQueryPart_Range)
    {
        whsReplenishmentTemplateLine.ProductVariantQuery = _packedQuery;
    }
    else if(menuItemName == WHSReplenishmentTemplatesLocationsToReplenishQueryPart_Range)
    {
        whsReplenishmentTemplateLine.LocationQuery = _packedQuery;
    }
    else if(menuItemName == WHSReplenishmentTemplatesZonesToReplenishQueryPart_Range)
    {
        whsReplenishmentTemplateLine.ZoneQuery = _packedQuery;
    }

    whsReplenishmentTemplateLine.write();
}

public void updateControls()
{
    Map enabledDisabledFields = WHSReplenishmentTemplateLine::fieldIdsToEnableDisableForReplenishmentType(whsReplenishmentTemplate.ReplenishmentType);

    var enabledDisabledFieldsEnumerator = enabledDisabledFields.getEnumerator();
    while (enabledDisabledFieldsEnumerator.moveNext())
    {
        whsReplenishmentTemplateLine_ds.object(enabledDisabledFieldsEnumerator.currentKey()).visible(enabledDisabledFieldsEnumerator.currentValue());
    }

    buttonLocationSelection.enabled(WHSReplenishmentTemplateLine::isLocationQueryEnabledForReplenishmentType(whsReplenishmentTemplate.ReplenishmentType));

    whsReplenishmentTemplate_ds.object(fieldnum(WHSReplenishmentTemplate, WaveStepCode)).enabled(whsReplenishmentTemplate.isWaveStepCodeApplicable());
    whsReplenishmentTemplate_ds.object(fieldnum(WHSReplenishmentTemplate, UseWorkForWaveReplen)).enabled(whsReplenishmentTemplate.isUseWorkForWaveReplenApplicable());
    whsReplenishmentTemplate_ds.object(fieldNum(WHSReplenishmentTemplate, CancelReplenWhenDemandCanceled)).enabled(whsReplenishmentTemplate.isCancelReplenWhenDemandCanceledApplicable());

    whsReplenishmentTemplateUOM.enabled(WHSReplenishmentTemplate.UseExistingImmediateReplenishmentWorkCrossUnit);
}

private void updateLineControls()
{
    var enableZoneSelection = whsReplenishmentTemplateLine.ReplenishmentThresholdScope != WHSReplenishmentThresholdScope::WMSLocation;
    var enableLocationSelection = !whsReplenishmentTemplateLine.ReplenFixedOnly
        && whsReplenishmentTemplateLine.ReplenishmentThresholdScope == WHSReplenishmentThresholdScope::WMSLocation
        && WHSReplenishmentTemplateLine::isLocationQueryEnabledForReplenishmentType(whsReplenishmentTemplate.ReplenishmentType);

    ButtonZoneSelection.enabled(enableZoneSelection);
    ButtonLocationSelection.enabled(enableLocationSelection);

    this.applyReplenishmentThresholdScope(whsReplenishmentTemplateLine.ReplenishmentThresholdScope);
    this.applyProductQueryMode(whsReplenishmentTemplateLine.ProductQueryMode);
}

private void applyReplenishmentThresholdScope(WHSReplenishmentThresholdScope _replenishmentThresholdScope)
{
    if (_replenishmentThresholdScope == WHSReplenishmentThresholdScope::WMSLocation)
    {
        whsReplenishmentTemplateLine_ds.object(fieldNum(WHSReplenishmentTemplateLine, EvalEmptyFixed)).enabled(true);
        whsReplenishmentTemplateLine_ds.object(fieldNum(WHSReplenishmentTemplateLine, ProductQueryMode)).enabled(true);
        whsReplenishmentTemplateLine_ds.object(fieldNum(WHSReplenishmentTemplateLine, ReplenFixedOnly)).enabled(true);
    }
    else
    {
        whsReplenishmentTemplateLine_ds.object(fieldNum(WHSReplenishmentTemplateLine, EvalEmptyFixed)).enabled(false);
        whsReplenishmentTemplateLine_ds.object(fieldNum(WHSReplenishmentTemplateLine, ProductQueryMode)).enabled(false);


        // do not disable the checkbox if already selected, so user can deselect it
        // when user tries to save the record, warning message will prompt the user to change the configuration
        whsReplenishmentTemplateLine_ds.object(fieldNum(WHSReplenishmentTemplateLine, ReplenFixedOnly)).enabled(whsReplenishmentTemplateLine.ReplenFixedOnly);


        whsReplenishmentTemplateLine.ProductQueryMode = WHSProductQueryMode::Item;
    }

    this.initWarehouseSelectionFields();
}

private void applyProductQueryMode(WHSProductQueryMode _productQueryMode)
{
    if (_productQueryMode == WHSProductQueryMode::Item)
    {
        buttonProductVariantSelection.enabled(false);
        buttonItemSelection.enabled(true);
    }
    else if (_productQueryMode == WHSProductQueryMode::Variant)
    {
        buttonItemSelection.enabled(false);
        buttonProductVariantSelection.enabled(true);
    }
}

public WHSWorkTransType getWorkTransType()
{
    return WHSWorkTransType::Replenishment;
}

private void initWarehouseSelectionFields()
{
    boolean thresholdScopeIsZone = WHSReplenishmentTemplateLine.ReplenishmentThresholdScope == WHSReplenishmentThresholdScope::Zone;
    whsReplenishmentTemplateLine_ds.object(fieldNum(WHSReplenishmentTemplateLine, WarehouseGroupAll)).enabled(thresholdScopeIsZone);

    this.initWarehouseGroupIdField(thresholdScopeIsZone);

    this.initWarehouseIdField(thresholdScopeIsZone);

}

private void initWarehouseGroupIdField(boolean _thresholdScopeIsZone)
{
    boolean shouldWarehouseGroupIdBeEnabledAndMandatory = _thresholdScopeIsZone
                && WHSReplenishmentTemplateLine.WarehouseGroupAll == WhsWarehouseGroupAll::Group;

    whsReplenishmentTemplateLine_ds.object(fieldNum(WHSReplenishmentTemplateLine, WarehouseGroupId))
        .enabled(shouldWarehouseGroupIdBeEnabledAndMandatory);

    whsReplenishmentTemplateLine_ds.object(fieldNum(WHSReplenishmentTemplateLine, WarehouseGroupId))
        .mandatory(shouldWarehouseGroupIdBeEnabledAndMandatory);
}

private void initWarehouseIdField(boolean _thresholdScopeIsZone)
{
    boolean shouldWarehouseIdBeEnabledAndMandatory = _thresholdScopeIsZone
                && WHSReplenishmentTemplateLine.WarehouseGroupAll == WhsWarehouseGroupAll::Warehouse;

    whsReplenishmentTemplateLine_ds.object(fieldNum(WHSReplenishmentTemplateLine, InventLocationId))
        .enabled(shouldWarehouseIdBeEnabledAndMandatory);

    whsReplenishmentTemplateLine_ds.object(fieldNum(WHSReplenishmentTemplateLine, InventLocationId))
        .mandatory(shouldWarehouseIdBeEnabledAndMandatory);
}

