/// <summary>
/// Enable or disable necessary controls
/// </summary>
public void modified()
{
    super();

    if (WHSReplenishmentTemplateLine.ReplenishmentThresholdScope == WHSReplenishmentThresholdScope::WMSLocation)
    {
        whsReplenishmentTemplateLine_ds.object(fieldNum(WHSReplenishmentTemplateLine, EvalEmptyFixed)).enabled(true);
        whsReplenishmentTemplateLine_ds.object(fieldNum(WHSReplenishmentTemplateLine, ReplenFixedOnly)).enabled(true);
        whsReplenishmentTemplateLine_ds.object(fieldNum(WHSReplenishmentTemplateLine, ProductQueryMode)).enabled(true);
        ButtonLocationSelection.enabled(true);
        ButtonZoneSelection.enabled(false);
    }
    else
    {
        whsReplenishmentTemplateLine_ds.object(fieldNum(WHSReplenishmentTemplateLine, EvalEmptyFixed)).enabled(false);
        whsReplenishmentTemplateLine_ds.object(fieldNum(WHSReplenishmentTemplateLine, ReplenFixedOnly)).enabled(false);
        whsReplenishmentTemplateLine_ds.object(fieldNum(WHSReplenishmentTemplateLine, ProductQueryMode)).enabled(false);
        ButtonItemSelection.enabled(true);
        ButtonProductVariantSelection.enabled(false);
        WHSReplenishmentTemplateLine.ProductQueryMode = WHSProductQueryMode::Item;
        ButtonLocationSelection.enabled(false);
        ButtonZoneSelection.enabled(true);
    }
}

void clicked()
{
    if (WHSEditQuerySetDefaultQueryRunFlight::instance().isEnabled())
    {
        QueryName queryName = whsReplenishmentTemplateLine.ReplenFixedOnly ? "@WAX:ProductQueryFixedLocations" : "@SYP4980032";

        SysQueryRun queryRun = WHSFormHelper::initializeQueryRunForEditingPackedDefaultQueryRun(whsReplenishmentTemplateLine.ItemQuery, 
                                                                                                queryName, 
                                                                                                WHSReplenishmentTemplateLine.initItemQuery());

        if (queryRun.prompt())
        {
            whsReplenishmentTemplateLine.ItemQuery = queryRun.pack();

            productsRangePart.refresh();
        }
    }
    else
    {
        QueryRun    queryRun;

        try
        {
            if (WHSReplenishmentTemplateLine.ItemQuery == conNull())
            {
                queryRun = new queryRun(WHSReplenishmentTemplateLine.initItemQuery());
            }
            else
            {
                queryRun = new QueryRun(whsReplenishmentTemplateLine.ItemQuery);
            }
        }
        catch (Exception::Internal)
        {
            queryRun = new queryRun(WHSReplenishmentTemplateLine.initItemQuery());
            info("@WAX2571");
        }

        // We need different name for the query to get reset() working with SysLastValue
        if (whsReplenishmentTemplateLine.ReplenFixedOnly)
        {
            queryRun.name("@WAX:ProductQueryFixedLocations");
        }
        else
        {
            queryRun.name("@SYP4980032");
        }

        queryRun.saveUserSetup(false);

        if (queryRun.prompt())
        {
            whsReplenishmentTemplateLine.ItemQuery = queryRun.pack();

            productsRangePart.refresh();

        }
    }

    super();
}

