void clicked()
{
    if (WHSEditQuerySetDefaultQueryRunFlight::instance().isEnabled())
    {
        SysQueryRun queryRun = WHSFormHelper::initializeQueryRunForEditing(whsReplenishmentTemplateLine.LocationQuery, "@SYP4980039", WHSReplenishmentTemplateLine.defaultLocationQueryRun());

        if (queryRun.prompt())
        {
            whsReplenishmentTemplateLine.LocationQuery = queryRun.pack();
            loc2ReplRangePart.refresh();                
        }
    }
    else
    {
        QueryRun    queryRun;

        try
        {
            queryRun = new QueryRun(whsReplenishmentTemplateLine.LocationQuery);
        }
        catch (Exception::Internal)
        {
            queryRun = new QueryRun(queryStr(WHSWMSLocation));
            info("@WAX2571");
        }

        queryRun.name("@SYP4980039");
        queryRun.saveUserSetup(false);

        if (queryRun.prompt())
        {
            whsReplenishmentTemplateLine.LocationQuery = queryRun.pack();
            loc2ReplRangePart.refresh();

        }
    }

    super();
}

/// <summary>
/// Set the Zone Query
/// </summary>
public void clicked()
{
    QueryRun queryRun;

    try
    {
        queryRun = new QueryRun(whsReplenishmentTemplateLine.ZoneQuery);
    }
    catch (Exception::Internal)
    {
        queryRun = new QueryRun(queryStr(WHSZoneQuery));
        info("@WAX2571");
    }

    queryRun.name("@WAX:ZoneQuery");
    queryRun.saveUserSetup(false);

    if (queryRun.prompt())
    {
        whsReplenishmentTemplateLine.ZoneQuery = queryRun.pack();

        zone2ReplRangePart.refresh();

    }

    super();
}

