Method.Source:
[Form]
public class WHSShipConsolidationProposal extends FormRun
{
    private WHSShipConsolidationSession shipConsolidationSession;
    private WHSShipConsolidationEngine  shipConsolidationEngine;

}

public void init()
{
    if (!element.args())
    {
        throw error("@SYS22539");
    }

    shipConsolidationEngine = element.args().parmObject() as WHSShipConsolidationEngine;

    if (!shipConsolidationEngine)
    {
        throw error("@SYS132237");
    }

    shipConsolidationSession = WHSShipConsolidationSession::find(shipConsolidationEngine.parmShipConsolidationSessionId());

    if (shipConsolidationSession.SessionState != WHSShipConsolidationSessionState::ShipmentsProposed)
    {
        throw error(strFmt("@WAX:TheShipmentConsolidationSessionIsInAnUnexpectedState", shipConsolidationSession.SessionState, WHSShipConsolidationSessionState::ShipmentsProposed));
    }

    super();
}

/// <summary>
/// Return a shipment consolidation session id.
/// </summary>
/// <returns>Shipment consolidation session id.</returns>
internal WHSShipConsolidationSessionRecId getShipConsolidationSessionId()
{
    return shipConsolidationSession.RecId;
}

/// <summary>
/// Return an instance of the <c>WHSShipConsolidationEngine</c> class.
/// </summary>
/// <returns>Instance of the <c>WHSShipConsolidationEngine</c> class</returns>
internal WHSShipConsolidationEngine getShipConsolidationEngine()
{
    return shipConsolidationEngine;
}

    /// <summary>
    /// Refreshes the proposed shipments data source
    /// </summary>
    internal void researchProposedShipments(RefRecId _baseProposedShipmentRecId = 0)
    {
        int pos = WHSShipConsolidationProposedShipmentContent_ds.getPosition();

		whsShipConsolidationProposedShipment_ds.research(true);
        whsShipConsolidationProposedShipmentContentNoRef_ds.research(true);

        if (_baseProposedShipmentRecId)
        {
            whsShipConsolidationProposedShipment_ds.positionToRecordByValue(fieldNum(WHSShipConsolidationProposedShipment, RecId), queryValue(_baseProposedShipmentRecId));
        }
		else
        {
			WHSShipConsolidationProposedShipmentContent_ds.setPosition(pos);
        }
    }

public void close()
{
    super();

    WHSShipConsolidationSession::cleanUpSession(shipConsolidationSession.RecId);
}

private void enableUnselectedButtons()
{
    AddToProposedShipment.enabled(whsShipConsolidationProposedShipment.RecId);
}

public void init()
{
    super();

    this.queryBuildDataSource().addRange(fieldNum(WHSShipConsolidationProposedShipment, ShipConsolidationSession)).value(SysQuery::value(shipConsolidationSession.RecId));
}

public void executeQuery()
{
    super();

    element.enableUnselectedButtons();
}

public void init()
{
    super();

    this.queryBuildDataSource().addRange(fieldNum(WHSShipConsolidationProposedShipmentContent, ShipConsolidationSession)).value(SysQuery::value(shipConsolidationSession.RecId));
    this.queryBuildDataSource().addRange(fieldNum(WHSShipConsolidationProposedShipmentContent, ProposedShipment)).value(SysQuery::valueNot(0));
}

public void init()
{
    super();

    this.queryBuildDataSource().clearLinks();
    this.queryBuildDataSource().clearDynalinks();

    this.queryBuildDataSource().addRange(fieldNum(WHSShipConsolidationProposedShipmentContent, ShipConsolidationSession)).value(SysQuery::value(shipConsolidationSession.RecId));
    this.queryBuildDataSource().addRange(fieldNum(WHSShipConsolidationProposedShipmentContent, ProposedShipment)).value(SysQuery::value(0));
}

public void init()
{
    super();

    this.queryBuildDataSource().addRange(fieldNum(WHSShipConsolidationProposedShipmentContent, ShipConsolidationSession)).value(SysQuery::value(shipConsolidationSession.RecId));
    this.queryBuildDataSource().addRange(fieldNum(WHSShipConsolidationProposedShipmentContent, ProposedShipment)).value(SysQuery::valueNot(0));
}

void clicked()
{
    shipConsolidationEngine.createConsolidatedShipmentsAsync(WHSShipConsolidationWorkbenchForm::getFormDataSourceSelectedShipmentIds(whsShipConsolidationProposedShipment_ds));

    if (!WHSShipConsolidationSession::exist(shipConsolidationSession.RecId))
    {
        //no sessions are available so close the form
        element.close();
    }
    else
    {
        // Refresh the form data - this assumes synchronous call to the TMSLoadBuildCreateLoadsService
        element.researchProposedShipments();
    }
}

void clicked()
{
    super();

    Set selectedRecordIds = WHSShipConsolidationWorkbenchForm::getFormDataSourceSelectedRecordIds(whsShipConsolidationProposedShipment_ds);

    if (selectedRecordIds && !selectedRecordIds.empty())
    {
        shipConsolidationEngine.removeProposedShipments(selectedRecordIds);

        element.researchProposedShipments();
    }
}

public void clicked()
{
    super();

    element.researchProposedShipments();
}

void clicked()
{
    super();

    Set selectedRecordIds = WHSShipConsolidationWorkbenchForm::getFormDataSourceSelectedRecordIds(whsShipConsolidationProposedShipmentContent_ds);

    if (selectedRecordIds && !selectedRecordIds.empty())
    {
        shipConsolidationEngine.removeReferenceToProposedShipment(selectedRecordIds);

        element.researchProposedShipments();
    }
}

void clicked()
{
    super();

    shipConsolidationEngine.setContentAsBaseProposedShipment(whsShipConsolidationProposedShipmentContent.RecId);

    element.researchProposedShipments();
}

void clicked()
{
    super();

    Set selectedRecordIds = WHSShipConsolidationWorkbenchForm::getFormDataSourceSelectedRecordIds(whsShipConsolidationProposedShipmentContent_ds);

    if (selectedRecordIds && !selectedRecordIds.empty())
    {
        shipConsolidationEngine.createNewBaseProposedShipmentFromContent(selectedRecordIds);

        element.researchProposedShipments();
    }
}

public void clicked()
{
    super();

    element.researchProposedShipments();
}

void clicked()
{
    super();

    Set selectedRecordIds = WHSShipConsolidationWorkbenchForm::getFormDataSourceSelectedRecordIds(whsShipConsolidationProposedShipmentContentNoRef_ds);

    if (selectedRecordIds && !selectedRecordIds.empty() && whsShipConsolidationProposedShipment.ShipmentId)
    {
        shipConsolidationEngine.setReferenceToProposedShipment(selectedRecordIds, whsShipConsolidationProposedShipment.ShipmentId);

        element.researchProposedShipments();
    }
}

void clicked()
{
    super();

    Set selectedRecordIds = WHSShipConsolidationWorkbenchForm::getFormDataSourceSelectedRecordIds(whsShipConsolidationProposedShipmentContentNoRef_ds);

    if (selectedRecordIds && !selectedRecordIds.empty())
    {
        RefRecId	baseProposedShipmentRecId;

        baseProposedShipmentRecId = shipConsolidationEngine.createNewBaseProposedShipmentFromContent(selectedRecordIds);

        element.researchProposedShipments(baseProposedShipmentRecId);
    }
}
