Method.Source:
[Form]
public class WHSWaveTable extends FormRun
{
    QueryBuildRange                     qbrLoad;
    WHSWaveTableForm                    whsWaveTableForm;

}

public void init()
{
    whsWaveTableForm = WHSWaveTableForm::construct();

    if (element.args().dataset() == tableNum(WHSLoadTable))
    {
        WHSLoadTable loadTable = element.args().record();
        whsWaveTableForm.setWaveId(loadTable.(fieldNum(WHSLoadTable, LoadId)));
    }
    else if (element.args().dataset() == tableNum(WHSWaveTable))
    {
        WHSWaveTable waveTable = element.args().record();
        whsWaveTableForm.parmWaveIds(waveTable.(fieldNum(WHSWaveTable, WaveId)));
    }

    super();
}

public void run()
{
    #Task

    if (!whsWaveTableForm.run(this.args().record()))
    {
        super();
    }

    if (whsWaveTableForm.parmWaveIds() != SysQuery::valueEmptyString()
        && !whsWaveTableForm.onlyOneWave())
    {
        element.task(#taskSwitchToGridView);
    }
}

public void init()
{
    super();

    if (whsWaveTableForm.parmWaveIds())
    {
        qbrLoad = this.query().dataSourceTable(tableNum(WHSWaveTable)).addRange(fieldNum(WHSWaveTable, WaveId));
    }
}

public int active()
{
    int ret = super();

    whsWaveTable_ds.allowEdit(whsWaveTable.allowEdit());
    whsWaveTable_ds.allowDelete(whsWaveTable.allowDelete());
    executeButton.enabled(whsWaveTable.canExecute());
    releaseButton.enabled(whsWaveTable.canRelease());
    workButton.enabled(whsWaveTable.canViewWork());
    loadButton.enabled(WHSWaveLine.LoadId != '');
    cancelWorkButton.enabled(whsWaveTable.canCancelWork());
    printPickList.enabled(whsWaveTable.canPrintPickList());
    whsReverseWork.enabled(whsWaveTable.canReverseWork());
    whsWaveProgress.enabled(whsWaveTable.canViewProgress());
    whsWaveLabel.enabled(WHSWaveLabel::existWaveId(WHSWaveTable.WaveId));
    whsWaveBatchJobDetails.enabled(whsWaveTable.canViewBatch());

    return ret;
}

public void create(boolean _append = false)
{
    super(_append);

    whsWaveTable.WaveId = WHSWaveTable::getNextWaveId();
}

/// <summary>
/// Adds the shipment Ids associated with the selected waves as a parameter to the menu item.
/// </summary>
void clicked()
{
    this.parameters(whsWaveTableForm::getLoadIds(WHSWaveTable_ds));

    super();
}

/// <summary>
/// Adds the shipment Ids associated with the selected waves as a parameter to the menu item.
/// </summary>
void clicked()
{
    this.parameters(WHSWaveTableForm::getShipmentIds(WHSWaveTable_ds));

    super();
}
