public void queryUpdated(FormRun _sender, container _packedQuery)
{
    whsWaveTemplateTable.WaveTemplateQuery = _packedQuery;

    headerRangePart.refresh();
    headerSortingPart.refresh();
}

WHSPostMethod buildPostMethodTmpTable()
{
    WHSPostMethod                   tmpPostMethod;
    WHSWaveTemplateType             selectedType;

    selectedType = whsWaveTemplateType.selection();

    tmpPostMethod = whsWaveTemplateTableForm.insertTmpPostMethod(whsWaveTemplateTable, selectedType);

    return tmpPostMethod;
}

public void init()
{
    super();

    whsWaveTemplateTableForm = new WHSWaveTemplateTableForm();

    WaveTemplateGroupingGroup.visible(whsWaveTemplateTableForm.shouldGroupButtonBeShown(WHSWaveTemplateType.selection()));

    WHSSequenceNumberMoverForm::newFromDataSource(
            WHSWaveTemplateTable_ds,
            fieldStr(WHSWaveTemplateTable, WaveTemplateSequence),
            [fieldStr(WHSWaveTemplateTable, WaveTemplateType)])
        .initNewButtons(ButtonGroup);

    this.initWarehouseSelectionFields();
    this.initQueryFormParts();

}

private void initQueryFormParts()
{
    str fieldNameQuery = fieldId2Name(tableNum(whsWaveTemplateTable), fieldNum(whsWaveTemplateTable, WaveTemplateQuery));
    str fieldNameQueryName = fieldId2Name(tableNum(whsWaveTemplateTable), fieldNum(whsWaveTemplateTable, RecId));
    headerRangePart = WHSQueryHelper::initializeQueryFormPart(element, WHSWaveTemplateTableQueryPart_Range, fieldNameQuery, '%1', fieldNameQueryName, whsWaveTemplateTable.defaultWaveTemplateQueryRun(), true, false);
    headerSortingPart = WHSQueryHelper::initializeQueryFormPart(element, WHSWaveTemplateTableQueryPart_Sorting, fieldNameQuery, '%1', fieldNameQueryName, whsWaveTemplateTable.defaultWaveTemplateQueryRun(), false, true);
}

void validDisplay()
{
    if (whsWaveTemplateTable.Valid)
    {
        valid.text("@SYS8553");
    }
    else
    {
        valid.text("@WAX:InvalidTemplate");
    }
}

public void enableLineMoveButtons()
{
    boolean         disableFirst;
    boolean         disableLast;

    WHSBaseWaveStepMethod baseWaveStepMethod = WHSWaveStepMethodFactory::newBaseMethodFromName(whsWaveTemplateLine.PostMethodName);

    if (whsWaveTemplateLine_ds.getPosition() == 1 || baseWaveStepMethod)
    {
        disableFirst = true;
    }

    if (whsWaveTemplateLine_ds.getPosition() == whsWaveTemplateLine_ds.numberOfRowsLoaded() || baseWaveStepMethod)
    {
        disableLast = true;
    }

    boolean canEdit = !element.inViewMode() && whsWaveTemplateTable_ds.allowEdit();

    moveUp.enabled(canEdit && !disableFirst);
    moveDown.enabled(canEdit && !disableLast);
    removeMethod.enabled(canEdit && (!baseWaveStepMethod || whsWaveTemplateLine.canMethodBeRemoved()));
    addMethod.enabled(canEdit && whsPostMethod.RecId);
    whsWaveTemplateLine_ds.allowEdit(canEdit);
    whsPostMethod_ds.allowEdit(canEdit);
}

public int task(int _taskId)
{
    #Task
    int ret;

    ret = super(_taskId);

    if (_taskId == #taskEditRecord || _taskId == #taskDoneEditRecord)
    {
        element.enableLineMoveButtons();
    }

    return ret;
}

