private void waveTemplateUpdate()
{
    WHSWaveTemplateTable_ds.reread();
    WHSWaveTemplateTable_ds.refresh();

    element.validDisplay();
}

private void initWarehouseSelectionFields()
{
    WHSWaveTemplateTable_ds.object(fieldNum(WHSWaveTemplateTable, WarehouseGroupId)).mandatory(WHSWaveTemplateTable.WarehouseGroupAll == WhsWarehouseGroupAll::Group);
    WHSWaveTemplateTable_ds.object(fieldNum(WHSWaveTemplateTable, InventSiteId)).mandatory(WHSWaveTemplateTable.WarehouseGroupAll == WhsWarehouseGroupAll::Warehouse);
    WHSWaveTemplateTable_ds.object(fieldNum(WHSWaveTemplateTable, InventLocationId)).mandatory(WHSWaveTemplateTable.WarehouseGroupAll == WhsWarehouseGroupAll::Warehouse);

    WHSWaveTemplateTable_ds.object(fieldNum(WHSWaveTemplateTable, WarehouseGroupId)).visible(WHSWaveTemplateTable.WarehouseGroupAll == WhsWarehouseGroupAll::Group);
    WHSWaveTemplateTable_ds.object(fieldNum(WHSWaveTemplateTable, InventSiteId)).visible(WHSWaveTemplateTable.WarehouseGroupAll == WhsWarehouseGroupAll::Warehouse);
    WHSWaveTemplateTable_ds.object(fieldNum(WHSWaveTemplateTable, InventLocationId)).visible(WHSWaveTemplateTable.WarehouseGroupAll == WhsWarehouseGroupAll::Warehouse);
}

public void initValue()
{
    super();

    whsWaveTemplateTableForm.whsWaveTemplateTable_initValue(whsWaveTemplateTable);
}

public void init()
{
    QueryBuildDataSource    qbds;

    super();

    qbds = whsWaveTemplateTable_q.dataSourceTable(tableNum(WHSWaveTemplateTable));
    qbds.addSortField(fieldNum(WHSWaveTemplateTable, WaveTemplateSequence), SortOrder::Ascending);
    qbrType = qbds.addRange(fieldNum(WHSWaveTemplateTable, WaveTemplateType));             

    if (element.args())
    {
        Common argsLookupRecord = element.args().lookupRecord();

        if (argsLookupRecord
            && argsLookupRecord.TableId == tableNum(WHSWaveTemplateTable))
        {
            WHSWaveTemplateTable localTemplateTable = argsLookupRecord; 
            whsWaveTemplateType.selection(localTemplateTable.WaveTemplateType);
            qbds.addRange(fieldNum(WHSWaveTemplateTable, WaveTemplateName)).value(localTemplateTable.WaveTemplateName);
        }
    }

    qbrType.value(whsWaveTemplateType.valueStr());
    whsWaveTemplateTable_ds.executeQuery();

}

