public boolean modified()
{
    boolean             ret;
    boolean             enable;

    ret = super();

    qbrType.value(whsWaveTemplateType.valueStr());
    whsPostMethod.setTmpData(element.buildPostMethodTmpTable());

    enable = whsWaveTemplateType.selection() == WHSWaveTemplateType::Shipping;

    whsWaveTemplateTable_ds.object(fieldNum(WHSWaveTemplateTable, WaveShipmentLimit)).visible(enable);
    whsWaveTemplateTable_ds.object(fieldNum(WHSWaveTemplateTable, WaveLineThreshold)).visible(enable);
    whsWaveTemplateTable_ds.object(fieldNum(WHSWaveTemplateTable, AutoExecuteWaveAtThreshold)).visible(enable);
    whsWaveTemplateTable_ds.object(fieldNum(WHSWaveTemplateTable, WaveMaxWeight)).visible(enable);

    whsWaveTemplateTable_ds.executeQuery();

    WaveTemplateGroupingGroup.visible(whsWaveTemplateTableForm.shouldGroupButtonBeShown(this.selection()));
    element.initWarehouseSelectionFields();

    return ret;
}

void clicked()
{
    super();

    WHSWaveTemplateLine::createWaveTemplateLine(whsWaveTemplateTable.WaveTemplateName, whsPostMethod);

    if (!whsPostMethod.RepeatableStep)
    {
        whsWaveTemplateTableForm.deleteWHSPostMethod(whsPostMethod);
    }

    whsWaveTemplateTable_ds.research(true);
    whsPostMethod_ds.executeQuery();
}

void clicked()
{            
    super();

    ttsbegin;

    boolean isMethodReplaced;
    WHSWaveStepMethod waveStepMethod= WHSWaveStepMethodFactory::newMethodFromName(whsWaveTemplateLine.PostMethodName);
    if (waveStepMethod)
    {
        WHSCustomWaveStepMethod customWaveStepMethod = waveStepMethod as WHSCustomWaveStepMethod;
        if (customWaveStepMethod)
        {
            var baseWaveStepMethod = customWaveStepMethod.baseMethod();

            if (baseWaveStepMethod
                && !baseWaveStepMethod.isOptional()
                && WHSCustomWaveStepMethod::isReplacementForBaseMethod(baseWaveStepMethod.name(), whsWaveTemplateLine.PostMethodName))
            {
                WHSWaveTemplateLine::createWaveTemplateLine(whsWaveTemplateTable.WaveTemplateName, WHSPostMethod::findByMethodName(baseWaveStepMethod.name()));
                isMethodReplaced = true;
            }
        }
    }

    if (!isMethodReplaced)
    {
        whsWaveTemplateTableForm.deleteWaveTemplateLine(whsWaveTemplateLine);
        whsPostMethod.setTmpData(element.buildPostMethodTmpTable());
    }

    whsWaveTemplateTable_ds.research(true);
    whsPostMethod_ds.research(true);
    ttscommit;
}

public void lookup()
{
    boolean successfullyPerformedWaveStepCodeLookup = whsWaveTemplateTableForm.performWaveStepCodeLookup(WHSPostMethod::findByMethodName(WHSWaveTemplateLine.PostMethodName), this);

    if (!successfullyPerformedWaveStepCodeLookup)
    {
        super();
    }
}

public void jumpRef()
{
    Args args = new Args();
    args.parm(WHSWaveTemplateLine.WaveStepCode);

    MenuFunction menuFunction = new MenuFunction(menuItemDisplayStr(WHSWaveStepCode), MenuItemType::Display);
    menuFunction.run(args);
}

