[Form]
public class WHSWorkTableListPage extends FormRun implements SysIFilterConsumerForm
{
    WHSWorkListPageFilter filter;
    Common recordArgs;
    WHSLoadTable loadTableArgs;
    WHSShipmentTable shipmentTableArgs;
    str workIdList;
    QueryBuildRange qbrWorkLineWorkId;
    boolean showClosedWork;
    QueryBuildRange qbrWorkStatus;
    WHSWorkTableListPageForm whsWorkTableListPageForm;

    private boolean drillDownFilterApplied;

}

public void init()
{
    whsWorkTableListPageForm = new WHSWorkTableListPageForm();
    boolean hasWorkListPageFilter = element.args().parmEnumType() == enumNum(WHSWorkListPageFilter);

    if (hasWorkListPageFilter)
    {
        filter = element.args().parmEnum();
    }

    if (element.args().record() && element.args().dataset() == tableNum(WHSLoadTable))
    {
        loadTableArgs = element.args().record();
    }

    if (element.args().record() && element.args().dataset() == tableNum(WHSShipmentTable))
    {
        shipmentTableArgs = element.args().record();
    }

    super();

    if (hasWorkListPageFilter)
    {
        showClosed.visible(false);
    }

    if (element.args().record() && element.args().dataset() == tableNum(WHSOutboundWorkLoadChartFilter))
    {
        WHSWorkLoadVisualizationFilter chartFilter = WHSWorkLoadVisualizationFilter::instantiate(this.form().name(), WHSWorkTable_ds, element.args().parmObject());
        chartFilter.applyFilters();
        showClosed.visible(false);
        drillDownFilterApplied = true;
    }
}

[SysObsolete('This functionality is no longer supported', false, 22\06\2022)]
public void enableBlockUnblockButton()
{

}

public void executeQuery()
{
    qbrWorkStatus = SysQuery::findOrCreateRange(this.queryBuildDataSource(), fieldNum(WHSWorkTable, WorkStatus));

    this.applyFilter();

    if (showClosedWork)
    {
        qbrWorkStatus.value('');
    }
    else
    {
        if (!qbrWorkStatus.value() && !drillDownFilterApplied)
        {
            qbrWorkStatus.value(strFmt('!%1, !%2, !%3', WHSWorkStatus::Closed, WHSWorkStatus::Cancelled, WHSWorkStatus::Combined));
        }
    }

    super();
}

        public void applyFilter()
        {
            QueryBuildRange qbrWorkType = SysQuery::findOrCreateRange(this.queryBuildDataSource(), fieldNum(WHSWorkTable, WorkTransType));

            if (element.args().parmEnumType() == enumNum(WHSWorkListPageFilter))
            {
				qbrWorkStatus.value(queryValue(this.getWorkStatusByFilter(filter)));
				qbrWorkType.value(this.getWorkTransTypeQueryValueByFilter(filter));
				return;
            }
        }

[Wrappable(true)] internal WHSWorkStatus getWorkStatusByFilter(WHSWorkListPageFilter _filter) { switch (_filter) { case WHSWorkListPageFilter::OpenPurch: case WHSWorkListPageFilter::OpenSales: case WHSWorkListPageFilter::OpenCycleCount: case WHSWorkListPageFilter::OpenReplen: case WHSWorkListPageFilter::OpenTransferReceipt: case WHSWorkListPageFilter::OpenReturn: case WHSWorkListPageFilter::OpenTransferIssue: case WHSWorkListPageFilter::OpenInventMovement: case WHSWorkListPageFilter::OpenShipment: case WHSWorkListPageFilter::OpenPackedContainerPicking: return WHSWorkStatus::Open;
case WHSWorkListPageFilter::ClosedPurch: case WHSWorkListPageFilter::ClosedSales: case WHSWorkListPageFilter::ClosedCycleCount: case WHSWorkListPageFilter::ClosedReplen: return WHSWorkStatus::Closed;
case WHSWorkListPageFilter::InProcessPurch: case WHSWorkListPageFilter::InProcessSales: case WHSWorkListPageFilter::InProcessCycleCount: case WHSWorkListPageFilter::InProcessReplen: case WHSWorkListPageFilter::InProcessTransferIssue: case WHSWorkListPageFilter::InProcessInventMovement: case WHSWorkListPageFilter::InProcessPackedContainerPicking: return WHSWorkStatus::InProcess;
case WHSWorkListPageFilter::PendingReviewCycleCount: return WHSWorkStatus::PendingReview;
default: return 0; } }

[Wrappable(true)]
internal str getWorkTransTypeQueryValueByFilter(WHSWorkListPageFilter _filter)
{
    switch (_filter)
    {
        case WHSWorkListPageFilter::OpenPurch:
        case WHSWorkListPageFilter::ClosedPurch:
        case WHSWorkListPageFilter::InProcessPurch:
            return queryValue(WHSWorkTransType::Purch);

        case WHSWorkListPageFilter::OpenSales:
        case WHSWorkListPageFilter::ClosedSales:
        case WHSWorkListPageFilter::InProcessSales:
            return queryValue(WHSWorkTransType::Sales);

        case WHSWorkListPageFilter::OpenCycleCount:
        case WHSWorkListPageFilter::ClosedCycleCount:
        case WHSWorkListPageFilter::InProcessCycleCount:
        case WHSWorkListPageFilter::PendingReviewCycleCount:
            return queryValue(WHSWorkTransType::CycleCount);

        case WHSWorkListPageFilter::OpenReplen:
        case WHSWorkListPageFilter::ClosedReplen:
        case WHSWorkListPageFilter::InProcessReplen:
            return queryValue(WHSWorkTransType::Replenishment);

        case WHSWorkListPageFilter::OpenTransferReceipt:
            return queryValue(WHSWorkTransType::TransferReceipt);

        case WHSWorkListPageFilter::OpenReturn:
            return queryValue(WHSWorkTransType::ReturnOrder);

        case WHSWorkListPageFilter::OpenTransferIssue:
        case WHSWorkListPageFilter::InProcessTransferIssue:
            return queryValue(WHSWorkTransType::TransferIssue);

        case WHSWorkListPageFilter::OpenInventMovement:
        case WHSWorkListPageFilter::InProcessInventMovement:
            return queryValue(WHSWorkTransType::Invent);

        case WHSWorkListPageFilter::OpenShipment:
            return WhsWorkTransactionType::workTransTypesForOutboundShipmentQueryRangeStr();

        case WHSWorkListPageFilter::OpenPackedContainerPicking:
        case WHSWorkListPageFilter::InProcessPackedContainerPicking:
            return queryValue(WHSWorkTransType::PackedContainerPicking);

        default:
            return '';
    }
}

public int active()
{
    int ret;

    ret = super();

    WHSChangeWorkLockedUser.enabled(WhsWorkTableForm::allowChangeWorkLockedUser(element));
    loadButton.enabled(whsWorkTable.LoadId != '');
    shipmentButton.enabled(whsWorkTable.ShipmentId != '');
    sourceButton.enabled(whsWorkTable.OrderNum != '');
    waveButton.enabled(whsWorkTable.WaveId != '');
    btnOutboundShipConfirm.enabled(WHSShipConfirmationAction::newFromBuffer(WHSLoadTable::find(WHSWorkTable.LoadId)).canEnableShipConfirm());
    workLineCycleCount.enabled(whsWorkTable.WorkTransType == WHSWorkTransType::CycleCount);
    whsReplenishmentWork.enabled(WHSReplenWorkLink::demandHasReplenishmentLink(whsWorkTable.WorkId));

    if (whsWorkDeferredPutProcessingTask.visible())
    {
        whsWorkDeferredPutProcessingTask.enabled(whsWorkTable.hasDeferredPutProcessingTasks());
    }

    element.enableBlockUnblockButton();
    element.enableSplitWorkFeature();
    WHSWaveLabel.enabled(WHSWaveLabel::existWorkId(WHSWorkTable.WorkId));

    if (qbrWorkLineWorkId)
    {
        qbrWorkLineWorkId.value(whsWorkTable.WorkId);
        whsWorkLine_ds.executeQuery();
    }

    return ret;
}

public void init()
{
    QueryBuildRange qbrWorkId;
    QueryBuildDataSource qbdWorkTable;
    QueryBuildDataSource qbdWorkLine;
    Query q;

    super();

    if (loadTableArgs || shipmentTableArgs)
    {
        element.createWorkIdList();

        q = new Query(queryStr(WHSWorkTableListPage));

        qbdWorkTable = whsWorkTable_q.dataSourceTable(tableNum(WHSWorkTable));
        qbdWorkTable.clearDynalinks();
        qbrWorkId = qbdWorkTable.addRange(fieldNum(WHSWorkTable, WorkId));
        qbrWorkId.value(workIdList);

        qbdWorkLine = whsWorkLine_q.dataSourceTable(tableNum(WHSWorkLine));
        qbdWorkLine.clearDynalinks();
        qbrWorkLineWorkId = qbdWorkLine.addRange(fieldNum(WHSWorkLine, WorkId));
    }
}

public boolean modified()
{
    boolean ret;

    ret = super();

    showClosedWork = showClosed.checked();
    whsWorkTable_ds.executeQuery();

    return ret;
}
