[Form]
public class WHSWorkTemplateTable extends FormRun implements WHSIQueryPartCallback
{
    private const str WHSWorkTemplateTableQueryPart_Range = menuItemDisplayStr(WHSWorkTemplateTableQueryPart_Range);
    private const str WHSWorkTemplateTableQueryPart_Sorting = menuItemDisplayStr(WHSWorkTemplateTableQueryPart_Sorting);
    private WHSIQueryPart headerRangePart;
    private WHSIQueryPart headerSortingPart;

    protected QueryBuildRange             workTransTypeRange;
    protected boolean                     initRange;
    protected LineNum                     maxLineNum;
    protected WHSWorkTemplateTableForm    whsWorkTemplateTableForm;
    protected int64                       workTemplateValidMessageId;

}

public void init()
{
    super();

    whsWorkTemplateTableForm = new WHSWorkTemplateTableForm();

    WHSSequenceNumberMoverForm::newFromDataSource(
            WHSWorkTemplateTable_ds,
            fieldStr(WHSWorkTemplateTable, WorkTemplatePriority),
            [fieldStr(WHSWorkTemplateTable, WorkTransType)])
        .initNewButtons(ButtonGroupHeader);

    ButtonMoveUpLine.visible(false);
    ButtonMoveDownLine.visible(false);

    WHSSequenceNumberMoverForm templateLineNumMover = WHSSequenceNumberMoverForm::newFromDataSource(
        WHSWorkTemplateLine_ds,
        fieldStr(WHSWorkTemplateLine, LineNum),
        [fieldStr(WHSWorkTemplateLine, WorkTemplateCode), fieldStr(WHSWorkTemplateLine, WorkTransType)]);
    templateLineNumMover.initNewButtons(ButtonGroupLine);
    templateLineNumMover.onMoved += eventhandler(this.workTemplateFormUpdate);

    this.setSelectedWorkTransType();

    if (whsWorkTemplateLine.WorkType == WHSWorkType::Custom)
    {
        whsWorkTemplateLine_WorkTypeCustomCode.enabled(true);
    }

    element.toggleButtons();

    this.initQueryFormParts();

}

private void initQueryFormParts()
{
    str fieldNameQuery = fieldId2Name(tableNum(whsWorkTemplateTable), fieldNum(whsWorkTemplateTable, WorkTemplateQuery));
    str fieldNameQueryName = fieldId2Name(tableNum(whsWorkTemplateTable), fieldNum(whsWorkTemplateTable, WorkTemplateCode));
    headerRangePart = WHSQueryHelper::initializeQueryFormPart(element, WHSWorkTemplateTableQueryPart_Range, fieldNameQuery, '%1', fieldNameQueryName, whsWorkTemplateTable.defaultPackedQueryRun(), true, false);
    headerSortingPart = WHSQueryHelper::initializeQueryFormPart(element, WHSWorkTemplateTableQueryPart_Sorting, fieldNameQuery, '%1', fieldNameQueryName, whsWorkTemplateTable.defaultPackedQueryRun(), false, true);
}

public void queryUpdated(FormRun _sender, container _packedQuery)
{
    QueryRun queryRun = new QueryRun(_packedQuery);

    if (whsWorkTemplateTableForm.shouldChangeQuery(whsWorkTemplateTable, workTransType.valueStr(), queryRun))
    {
        whsWorkTemplateTable.WorkTemplateQuery = _packedQuery;

        WHSWorkTemplateGroup::createGroups(
                        whsWorkTemplateTable.RecId,
                        queryRun,
                        whsWorkTemplateTable.getMandatoryGroupingFields());

        whsWorkTemplateTable.write();
    }

    headerRangePart.refresh();
    headerSortingPart.refresh();
}

private void setSelectedWorkTransType()
{
    var caller = element.args().caller();

    if (caller is WHSIWorkTemplateFormFilter)
    {
        WHSIWorkTemplateFormFilter workTemplateCaller = caller as WHSIWorkTemplateFormFilter;
        workTransType.selection(enum2int(workTemplateCaller.getWorkTransType()));
    }
    else
    {
        workTransType.selection(element.args().parmEnum());
    }
}

public void setMaxLineNum()
{
    maxLineNum = whsWorkTemplateTableForm.setMaxLineNum(whsWorkTemplateTable);
}

public void toggleButtons()
{
    if (whsWorkTemplateTable.RecId != 0)
    {
        buttonWHSWorkTemplateGroup.enabled(whsWorkTemplateTable.canSetWorkBreaks());
        buttonSelect.enabled(whsWorkTemplateTable.canSetWorkTemplateQuery());
        buttonCopy.enabled(true);

        WHSWorkTemplateTable_AutoExecute.enabled(whsWorkTemplateTable.canSetAutomaticallyProcess());
        TabPageGeneral.enabled(whsWorkTemplateTable.WorkTransType != WHSWorkTransType::CycleCount);

        ButtonNewWorkTemplateLine.enabled(true);
        ButtonDeleteWorkTemplateLine.enabled(true);

        ButtonReorganizeSequence.visible(true);
        ButtonReorganizeSequence.enabled(true);
    }
    else
    {
        buttonWHSWorkTemplateGroup.enabled(false);
        buttonSelect.enabled(false);
        buttonCopy.enabled(false);

        ButtonNewWorkTemplateLine.enabled(false);
        ButtonDeleteWorkTemplateLine.enabled(false);

        ButtonReorganizeSequence.visible(true);
        ButtonReorganizeSequence.enabled(false);
    }

    this.disableLineButtons();
}

public void disableLineButtons()
{
    ButtonMoveUpLine.enabled(false);
    ButtonMoveDownLine.enabled(false);

    WHSWorkTemplateLine_Mandatory.enabled(false);
    WHSWorkTemplateLine_WorkStop.enabled(false);
    WHSWorkTemplateLineGroup.enabled(false);
}

public void toggleLineButtons()
{
    boolean isTemplatePresent = whsWorkTemplateTable.RecId != 0;
    ButtonNewWorkTemplateLine.enabled(isTemplatePresent);
    ButtonDeleteWorkTemplateLine.enabled(isTemplatePresent);

    boolean isLinePresent = whsWorkTemplateLine.RecId != 0;
    ButtonMoveUpLine.enabled(isTemplatePresent && isLinePresent);
    ButtonMoveDownLine.enabled(isTemplatePresent && isLinePresent);

    WHSWorkTemplateLine_Mandatory.enabled(whsWorkTemplateLine.canSetMandatory());
    WHSWorkTemplateLine_WorkStop.enabled(whsWorkTemplateLine.canSetWorkStop());
    WHSWorkTemplateLineGroup.enabled(isTemplatePresent && whsWorkTemplateLine.canSetWorkLineBreaks());
}

void toggleMaximums()
{
    if (whsWorkTemplateTable.UsePackingQty)
    {
        whsWorkTemplateTable_MaxVolume.enabled(false);
        whsWorkTemplateTable_MaxWeight.enabled(false);
        whsWorkTemplateTable_MaxQty.enabled(false);
        whsWorkTemplateTable_MaxUnitId.enabled(false);
    }
    else
    {
        whsWorkTemplateTable_MaxVolume.enabled(true);
        whsWorkTemplateTable_MaxWeight.enabled(true);
        whsWorkTemplateTable_MaxQty.enabled(true);
        whsWorkTemplateTable_MaxUnitId.enabled(true);
    }
}

public void workTemplateFormUpdate()
{
    this.setMaxLineNum();

    this.toggleButtons();

    whsWorkTemplateTableForm.writeTemplateTable(whsWorkTemplateTable);

    whsWorkTemplateTable_ds.reread();
    whsWorkTemplateTable_ds.refresh();

    this.checkWorkTemplateValid();
}

private void removeWorkTemplateValidMessage()
{
    if (workTemplateValidMessageId)
    {
        Message::Remove(workTemplateValidMessageId);
        workTemplateValidMessageId = 0;
    }
}

private void checkWorkTemplateValid()
{
    this.removeWorkTemplateValidMessage();

    if (!whsWorkTemplateTable.WorkTemplateValid)
    {
        SysInfoLogStr warningMessage = (whsWorkTemplateTable.WorkTemplateErrorText) ? strFmt("@WAX:InvalidTemplateWithErrorMessage", whsWorkTemplateTable.WorkTemplateErrorText) : "@WAX:InvalidTemplate";

        workTemplateValidMessageId = Message::Add(MessageSeverity::Warning, warningMessage);
    }
}

public void initValue()
{
    super();

    whsWorkTemplateTable.initFromWorkTransType(any2Enum(workTransType.selection()));

    element.disableLineButtons();
}

public void delete()
{
    int selectedRows = whsWorkTemplateTable_ds.recordsMarked().lastIndex();

    super();

    element.toggleButtons();

    boolean lastTemplateDeleted = whsWorkTemplateTable.RecId == 0;

    if (!lastTemplateDeleted)
    {
        element.workTemplateFormUpdate();
    }
}

public void write()
{
    super();

    whsWorkTemplateTableForm.setQueryRun(whsWorkTemplateTable, whsWorkTemplateTable.WorkTransType);
    whsWorkTemplateTable.update();

    whsWorkTemplateTable_ds.research(true);
}

        public int active()
        {
            int ret = super();

            element.setMaxLineNum();

            element.toggleButtons();

            element.toggleMaximums();

            element.checkWorkTemplateValid();

            if (whsWorkTemplateTable.AutoExecute)
            {
				// Replenishment is part of itemRelatedPicking, but is not part of this check,
				// therefore this edge case must be handled differently.
				if (WhsWorkTransactionType::singletonFromTypeOrDefault(whsWorkTemplateTable.WorkTransType).isItemRelatedPicking()
					&& whsWorkTemplateTable.WorkTransType != WHSWorkTransType::Replenishment)
				{
					whsWorkTemplateTable_BatchAllocationStrategy.enabled(true);
				}
            }
            else
            {
                whsWorkTemplateTable_BatchAllocationStrategy.enabled(false);
            }

            WHSWorkTemplateTable_ds.object(fieldNum(WHSWorkTemplateTable, ReplenishmentDependentWorkBlockingPolicy)).visible(WhsWorkCreateReplenishment::isReplenishmentAllowedForWorkTransType(WHSWorkTemplateTable.WorkTransType));
            WHSWorkTemplateTable_ds.object(fieldNum(WHSWorkTemplateTable, SplitCrossDockWorkAtLicensePlateReceiving)).visible(WHSWorkTemplateTable.WorkTransType == WHSWorkTransType::CrossDocking);

            return ret;
        }

public void executeQuery()
{
    workTransTypeRange.value(workTransType.valueStr());

    if (initRange)
    {
        workTransTypeRange.value(workTransType.valueStr());
        initRange = false;
    }

    super();
}

public void init()
{
    super();

    workTransTypeRange = this.query().dataSourceTable(tableNum(WHSWorkTemplateTable)).addRange(fieldNum(WHSWorkTemplateTable, WorkTransType));
    initRange = true;
}

private void enableWHSWorkTemplateLine_LocDirHintCode()
{
    if (whsWorkTemplateLine.WorkType == WHSWorkType::Put)
    {
        WhsWorkTransactionType workTransactionType = WhsWorkTransactionType::singletonFromType(whsWorkTemplateTable.WorkTransType);

        if (workTransactionType)
        {
            if (workTransactionType.isOutbound())
            {
                this.enableLocDirHintCodeForOutboundTransactionType(workTransactionType);
            }
            else if (workTransactionType.isInbound()
                    || whsWorkTemplateTable.WorkTransType == WHSWorkTransType::Replenishment)
            {
                this.enableLocDirHintCodeForInboundTransactionType(workTransactionType);
            }
        }
    }
    else
    {
        whsWorkTemplateLine.LocDirHintCode = '';
        whsWorkTemplateLine_LocDirHintCode.enabled(false);
    }
}

private void enableLocDirHintCodeForInboundTransactionType(WhsWorkTransactionType _workTransactionType)
{
    //if we have a template code on the last put line so we need to be able to remove it if the type does not allow it
    if (whsWorkTemplateLine.WorkTemplateCode && whsWorkTemplateLine.isFinalPutLine() && _workTransactionType.validateLocDirCodeForWorkTemplateLine(whsWorkTemplateLine, true) != '')
    {
        whsWorkTemplateLine_LocDirHintCode.enabled(true);
    }
    else
    {
        WHSWorkTemplateLine templateLine;

        select firstonly RecId from templateLine
        where templateLine.WorkTemplateCode == whsWorkTemplateLine.WorkTemplateCode &&
              templateLine.LineNum           > whsWorkTemplateLine.LineNum          &&
              templateLine.WorkType         == WHSWorkType::Put;

        whsWorkTemplateLine_LocDirHintCode.enabled(templateLine.RecId);
    }
}

private void enableLocDirHintCodeForOutboundTransactionType(WhsWorkTransactionType _workTransactionType)
{
    if (whsWorkTemplateTable.WorkTransType == WHSWorkTransType::CrossDocking
        && whsWorkTemplateLine.isFinalPutLine()
        && _workTransactionType.validateLocDirCodeForWorkTemplateLine(whsWorkTemplateLine, true) == '')
    {
        whsWorkTemplateLine_LocDirHintCode.enabled(false);
    }
    else
    {
        whsWorkTemplateLine_LocDirHintCode.enabled(true);
    }
}

public void delete()
{
    int selectedRows = whsWorkTemplateLine_ds.recordsMarked().lastIndex();

    super();

    whsWorkTemplateTableForm.reorderWorkTemplateLines(whsWorkTemplateLine_ds,
        whsWorkTemplateTable.WorkTemplateCode,
        whsWorkTemplateTable.WorkTransType,
        selectedRows);

    element.workTemplateFormUpdate();
}

public void displayOption(Common _record, FormRowDisplayOption _options)
{
    WHSWorkTemplateLine locWorkTemplateLine = _record;
    WhsWorkTransactionType workTransactionType = WhsWorkTransactionType::singletonFromType(whsWorkTemplateTable.WorkTransType);

    if (workTransactionType)
    {
        WHSWorkTemplateErrorText errorText = workTransactionType.validateLocDirCodeForWorkTemplateLine(locWorkTemplateLine, locWorkTemplateLine.LineNum == maxLineNum);

        if (errorText)
        {
            whsWorkTemplateTable.WorkTemplateErrorText = errorText;
            whsWorkTemplateTable.WorkTemplateValid = false;
        }
    }

    super(_record, _options);
}

public void write()
{
    super();

    element.workTemplateFormUpdate();

    whsWorkTemplateLine_ds.research(true);
}

public void initValue()
{
    whsWorkTemplateLine.Mandatory = NoYes::Yes;
    whsWorkTemplateLine.LineNum = maxLineNum + 1;

    super();
}

public int active()
{
    int ret = super();

    whsWorkTemplateLine_WorkTypeCustomCode.enabled(whsWorkTemplateLine.WorkType == WHSWorkType::Custom);

    this.enableWHSWorkTemplateLine_LocDirHintCode();

    whsWorkTemplateLine_Freeze.enabled(whsWorkTemplateLine.WorkStop);
    WHSWorkTemplateLine_QualityCheckTemplateName.enabled(whsWorkTemplateLine.WorkType == WHSWorkType::QualityCheck);
    WHSWorkTemplateLine_AnchoringGroupId.enabled(whsWorkTemplateLine.WorkType == WHSWorkType::Put);

    element.toggleLineButtons();

    return ret;
}

void clicked()
{
    SysQueryRun queryRun = WHSFormHelper::initializeQueryRunForEditing(
        whsWorkTemplateTable.WorkTemplateQuery,
        whsWorkTemplateTable.WorkTemplateCode,
        whsWorkTemplateTable.defaultPackedQueryRun());

    if (queryRun.prompt())
    {
        if (whsWorkTemplateTableForm.shouldChangeQuery(whsWorkTemplateTable, workTransType.valueStr(), queryRun))
        {
            whsWorkTemplateTable.WorkTemplateQuery = queryRun.pack();
            WHSWorkTemplateGroup::createGroups(
                whsWorkTemplateTable.RecId,
                queryRun,
                whsWorkTemplateTable.getMandatoryGroupingFields());

             whsWorkTemplateTable.write();                                        
            headerRangePart.refresh();
            headerSortingPart.refresh();

        }
    }

    element.toggleButtons();
    element.toggleLineButtons();

    super();
}

void clicked()
{
    super();

    if (whsWorkTemplateTableForm.buttonCopyDialog(whsWorkTemplateTable))
    {
        whsWorkTemplateTable_ds.executeQuery();
        element.design().viewEditMode(ViewEditMode::Edit);
    }
}

void clicked()
{
    super();
    WHSWorkTemplateTable::reorderWorkTemplateSequenceNumbers(whsWorkTemplateTable.WorkTransType);

    whsWorkTemplateTable_ds.research(true);
}

public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        workTransTypeRange.value(workTransType.valueStr());
        whsWorkTemplateTable_ds.executeQuery();
        element.toggleButtons();
    }

    return ret;
}

public boolean validate()
{
    boolean ret = super();

    switch (this.valueStr())
    {
        case enum2str(WHSWorkTransType::WorkCancel):
            ret = checkFailed("@WAX4626");
            break;
    }

    return ret;
}

void clicked()
{
    whsWorkTemplateTableForm.setLineNumUpLine(whsWorkTemplateLine_ds);

    element.workTemplateFormUpdate();

    super();
}

void clicked()
{
    whsWorkTemplateTableForm.setLineNumDownLine(whsWorkTemplateLine_ds);

    element.workTemplateFormUpdate();

    super();
}

public boolean modified()
{
    boolean ret = super();

    whsWorkTemplateLine_WorkTypeCustomCode.enabled(whsWorkTemplateLine.WorkType == WHSWorkType::Custom);

    return ret;
}

public void lookup()
{
    WHSWorkClassTable::lookupWorkClassIdByWorkTransType(this, whsWorkTemplateTable.WorkTransType);
}
